import React from "react";
import BootstrapSwitchButton from 'bootstrap-switch-button-react'
export function dispHello (){
    return(
        <div>
            MERHABALAAAR BİLAL
        </div>
    )
}


function PreviousPermissons() {

    let currBol = true;
    if (currBol){
        console.log("S.A HOŞGELDİN")
    }
    else {
        console.log("BB DİYORUZ ")

    }

    return(
        <div style={{
            display:"flex",
            flexDirection:"column",
            flex:6
        }}>
            <div style={{
                display:"flex",
                flexDirection:"row",
                flex:1,
                justifyContent:"center",
                margin : "25px"
            }}>
                <BootstrapSwitchButton
                    style={{
                        display:"flex",
                        flex:1
                    }}
                    checked={true}
                    onlabel='AKTİF TALEPLER'
                    offlabel='GEÇMİŞ TALEPLER'
                    width={350}
                    onChange={() => {
                       currBol=false
                    }}
                />
            </div>
            <div style={{
                display:"flex",
                flexDirection:"row",
                flex:1,
                justifyContent:"flex-end",
                textAlign:"center",

            }}>
                <h1
                    style={{
                        display:"flex",
                        flexDirection:"row",
                        justifyContent:"center",
                        flex:0.6,
                        fontFamily:"Roboto",
                        fontSize:"15px",
                        textShadow: "0px 4px 4px rgba(0, 0, 0, 0.25)",
                        borderRight:"1px solid black",
                        textAlign:"center",
                        border:"1px solid black",
                        borderRadius:"3%",
                        margin:"2px",
                        paddingTop:"10px",
                        paddingBottom:"10px"
                    }}>
                   İZİN BİLGİLERİ
                </h1>
                <h1
                style={{
                    display:"flex",
                    flexDirection:"row",
                    justifyContent:"center",
                    flex:0.2,
                    fontFamily:"Roboto",
                    fontSize:"15px",
                    textShadow: "0px 4px 4px rgba(0, 0, 0, 0.25)",
                    textAlign:"center",
                    border:"1px solid black",
                    borderRadius:"3%",
                    margin:"2px",
                    paddingTop:"10px",
                    paddingBottom:"10px"

                }}>
                    AMİR ONAYI
                </h1>
                <h1
                    style={{
                        display:"flex",
                        flexDirection:"row",
                        justifyContent:"center",
                        flex:0.2,
                        fontFamily:"Scada",
                        fontSize:"15px",
                        textShadow: "0px 4px 4px rgba(0, 0, 0, 0.25)",
                        textAlign:"center",
                        border:"1px solid black",
                        borderRadius:"3%",
                        margin:"2px",
                        paddingTop:"10px",
                        paddingBottom:"10px"
                    }}>
                    GENEL YÖNETİCİ ONAYI
                </h1>

            </div>

        </div>


    )
}
export default PreviousPermissons;
