import React from "react";
import BootstrapSwitchButton from 'bootstrap-switch-button-react'
import {useSpring, animated} from "react-spring";
import {useState} from "react";
import {Bounce} from "react-reveal";
import {Link} from "react-router-dom";
import PermissionDisplay from "./PermissionDisplay";


export function acceptionSymb() {
    return (
        <div style={{
            display: "flex",
            margin: "auto",
            flex: 1,
            background: "rgb(39,180,64)",
            borderRadius: "4%"
        }}>
            <h1 style={{
                display: "flex",
                margin: "auto",
                fontSize: "20px"
            }}
            >
                ✔
            </h1>
        </div>
    )
}

export function deniedSymb() {
    return (
        <div style={{
            display: "flex",
            margin: "auto",
            flex: 1,
            background: "rgb(203,27,27)",
            borderRadius: "4%"
        }}>
            <h1 style={{
                display: "flex",
                margin: "auto",
                fontSize: "20px"
            }}
            >
                ✖
            </h1>
        </div>
    )
}

export function waitingSymbol() {
    return (
        <div style={{
            display: "flex",
            margin: "auto",
            flex: 1,
            background: "rgb(255,227,4)",
            borderRadius: "4%"
        }}>
            <h1 style={{
                display: "flex",
                margin: "auto",
                fontSize: "20px"
            }}
            >
                ⊖
            </h1>
        </div>
    )
}

function permissionLine(izinTalepID, izinTalepTarihi, izinBaşlangıcTarihi, izinBitisTarihi) {
    return (
        <label style={{
            justifyContent: "left",
            border: "0.5px solid black",
            flex: 7,
            borderRadius: "2%",
            display: "flex",
            background: "rgba(7,133,180,0.04)"
        }}
        >
            <h1 style={{
                display: "flex",
                flexDirection: "row",
                flex: 0.7,
                fontSize: "12px",
                margin: "2px",
                justifyContent: "center"
            }}>
                {izinTalepID}
            </h1>

            <h1 style={{
                display: "flex",
                flexDirection: "row",
                margin: "2px",
                flex: 1.3,
                fontSize: "12px",
                justifyContent: "center"
            }}>
                {izinTalepTarihi}
            </h1>

            <h1 style={{
                display: "flex",
                flexDirection: "row",
                flex: 1.3,
                fontSize: "12px",
                justifyContent: "center",
                margin: "2px"
            }}>
                {izinBaşlangıcTarihi}
            </h1>

            <h1 style={{
                display: "flex",
                flexDirection: "row",
                flex: 1.3,
                fontSize: "12px",
                margin: "2px",
                justifyContent: "center"
            }}>
                {izinBitisTarihi}
            </h1>

            <div style={{
                display: "flex",
                flexDirection: "row",
                flex: 1.5,
                fontSize: "12px",
                justifyContent: "center"
            }}>
                {waitingSymbol()}
            </div>

            <div style={{
                display: "flex",
                flexDirection: "row",
                flex: 1.5,
                fontSize: "12px",
                justifyContent: "center"
            }}>
                {deniedSymb()}
            </div>
        </label>
    )
}


export function activeDemands(props) {

    let izinTalepID = 22;
    let izinTalepTarihi = "22/22/2222";
    let izinBaşlangıcTarihi = "22/22/2222";
    let izinBitisTarihi = "22/22/2222";

    if (props) {
        return (
            <div style={{
                display: "flex",
                flexDirection: "column",
                justifyContent: "row",
                marginTop: "8px",
                border: "0.2px solid black",
                padding: "2px"
            }}>
                <Link to={PermissionDisplay} style={{textDecoration: "none"}}>
                    {permissionLine(izinTalepID, izinTalepTarihi, izinBaşlangıcTarihi, izinBitisTarihi)}
                </Link>

                {permissionLine(izinTalepID, izinTalepTarihi, izinBaşlangıcTarihi, izinBitisTarihi)}
                {permissionLine(izinTalepID, izinTalepTarihi, izinBaşlangıcTarihi, izinBitisTarihi)}
                {permissionLine(izinTalepID, izinTalepTarihi, izinBaşlangıcTarihi, izinBitisTarihi)}
                {permissionLine(izinTalepID, izinTalepTarihi, izinBaşlangıcTarihi, izinBitisTarihi)}
                {permissionLine(izinTalepID, izinTalepTarihi, izinBaşlangıcTarihi, izinBitisTarihi)}
                {permissionLine(izinTalepID, izinTalepTarihi, izinBaşlangıcTarihi, izinBitisTarihi)}
                {permissionLine(izinTalepID, izinTalepTarihi, izinBaşlangıcTarihi, izinBitisTarihi)}
                {permissionLine(izinTalepID, izinTalepTarihi, izinBaşlangıcTarihi, izinBitisTarihi)}

            </div>
        )
    } else {
        let eskiIzinTalepID = 213;
        let eskiIzinTalepTarihi = "11/11/1111";
        let eskiIzinBaşlangıcTarihi = "11/11/1111";
        let eskiIzinBitisTarihi = "11/11/1111";
        return (
            <div style={{
                display: "flex",
                flexDirection: "column",
                justifyContent: "row",
                marginTop: "8px",
                border: "0.2px solid black",
                padding: "2px"
            }}>
                {permissionLine(eskiIzinTalepID, eskiIzinTalepTarihi, eskiIzinBaşlangıcTarihi, eskiIzinBitisTarihi)}
                {permissionLine(eskiIzinTalepID, eskiIzinTalepTarihi, eskiIzinBaşlangıcTarihi, eskiIzinBitisTarihi)}
                {permissionLine(eskiIzinTalepID, eskiIzinTalepTarihi, eskiIzinBaşlangıcTarihi, eskiIzinBitisTarihi)}
                {permissionLine(eskiIzinTalepID, eskiIzinTalepTarihi, eskiIzinBaşlangıcTarihi, eskiIzinBitisTarihi)}
