import React from "react";
import BootstrapSwitchButton from 'bootstrap-switch-button-react'
import {useSpring, animated} from "react-spring";
import {useState} from "react";
import {Bounce} from "react-reveal";
import {Link} from "react-router-dom";
import PermissionDisplay from "./PermissionDisplay";


export function acceptionSymb() {
    return (
        <div style={{
            display: "flex",
            margin: "auto",
            flex: 1,
            background: "rgb(39,180,64)",
            borderRadius: "4%"
        }}>
            <h1 style={{
                display: "flex",
                margin: "auto",
                fontSize: "20px"
            }}
            >
                ✔
            </h1>
        </div>
    )
}

export function deniedSymb() {
    return (
        <div style={{
            display: "flex",
            margin: "auto",
            flex: 1,
            background: "rgb(203,27,27)",
            borderRadius: "4%"
        }}>
            <h1 style={{
                display: "flex",
                margin: "auto",
                fontSize: "20px"
            }}
            >
                ✖
            </h1>
        </div>
    )
}

export function waitingSymbol() {
    return (
        <div style={{
            display: "flex",
            margin: "auto",
            flex: 1,
            background: "rgb(255,227,4)",
            borderRadius: "4%"
        }}>
            <h1 style={{
                display: "flex",
                margin: "auto",
                fontSize: "20px"
            }}
            >
                ⊖
            </h1>
        </div>
    )
}

function permissionLine(izinTalepID, izinTalepTarihi, izinBaslangicTarihi, izinBitisTarihi) {
    return (
        <Link to="PermissionDisplay" style={{textDecoration: "none", display: "flex", flex: 7}}>
            <button type="button" style={{display: "flex", flex: 7, border: "0px", margin: "auto"}}>
                <label style={{
                    justifyContent: "left",
                    border: "0.5px solid black",
                    flex: 7,
                    borderRadius: "2%",
                    display: "flex",
                    background: "rgba(7,133,180,0.04)"
                }}
                >
                    <h1 style={{
                        display: "flex",
                        flexDirection: "row",
                        flex: 0.7,
                        fontSize: "12px",
                        margin: "2px",
                        justifyContent: "center"
                    }}>
                        {izinTalepID}
                    </h1>

                    <h1 style={{
                        display: "flex",
                        flexDirection: "row",
                        margin: "2px",
                        flex: 1.3,
                        fontSize: "12px",
                        justifyContent: "center"
                    }}>
                        {izinTalepTarihi}
                    </h1>

                    <h1 style={{
                        display: "flex",
