import React from "react";
import BootstrapSwitchButton from 'bootstrap-switch-button-react'
import {useSpring, animated} from "react-spring";
import {useState} from "react";
import {Bounce} from "react-reveal";
import {Link} from "react-router-dom";
import PermissionDisplay from "./PermissionDisplay";
import Row from "react-bootstrap/Row";
import StickyHeadTable from "./StickyHeadTable";


export function acceptionSymb() {
    return (
        <div style={{
            display: "flex",
            margin: "auto",
            flex: 1,
            background: "rgb(0,190,55)",
            borderRadius: "50%"
        }}>
            <h1 style={{
                display: "flex",
                margin: "auto",
                fontSize: "20px",
                borderRadius: "50%"
            }}
            >
                ✔
            </h1>
        </div>
    )
}

export function deniedSymb() {
    return (
        <div style={{
            display: "flex",
            margin: "auto",
            flex: 1,
            background: "rgb(203,27,27)",
            borderRadius: "50%"
        }}>
            <h1 style={{
                display: "flex",
                margin: "auto",
                fontSize: "20px"
            }}
            >
                ✖
            </h1>
        </div>
    )
}

export function waitingSymbol() {
    return (
        <div style={{
            display: "flex",
            margin: "auto",
            flex: 1,
            background: "rgb(255,227,4)",
            borderRadius: "50%"
        }}>
            <h1 style={{
                display: "flex",
                margin: "auto",
                fontSize: "20px"
            }}
            >
                ⊖
            </h1>
        </div>
    )
}

function activeDemands(displayActives) {
    if (displayActives) {
        return (
            <StickyHeadTable/>
        )
    } else {
        return null;
    }
}

class PreviousPermissons extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            displayActives: true,
        }

        this.ToggleButton = this.ToggleButton.bind(this);
    }

    ToggleButton() {
        this.setState((currentState) => ({
            displayActives: !this.state.displayActives
        }));
    }

    render() {
        return (
            <div style={{
                display: "flex",
                flexDirection: "column",
                flex: 6,
                justifyContent:"center"
            }}>
                <BootstrapSwitchButton
                    width={300}
                    checked={this.state.displayActives}
                    onlabel='AKTİF TALEPLER'
                    offlabel='GEÇMİŞ TALEPLER'
                    onChange={() => this.ToggleButton()}
                />
                <div style={{
                    display: "flex",
                    flex: 5,
                    width: "100%"
                }}>
                    {
                        activeDemands(this.state.displayActives)
                    }
                </div>

            </div>
        )
    }
}

export default PreviousPermissons;
