import React from "react";
import {Link} from "react-router-dom";
import {Row} from "react-bootstrap";
import Col from "react-bootstrap/Col";
import ThemeProvider from "react-bootstrap/ThemeProvider";
import Button from "@material-ui/core/Button";
import createMuiTheme from "@material-ui/core/styles/createMuiTheme";
import makeStyles from "@material-ui/core/styles/makeStyles";
import green from "@material-ui/core/colors/green";
import withStyles from "@material-ui/core/styles/withStyles";

const BootstrapButton = withStyles({
    root: {
        boxShadow: 'none',
        textTransform: 'none',
        fontSize: 16,
        padding: '6px 12px',
        border: '1px solid',
        lineHeight: 1.5,
        backgroundColor: '#0063cc',
        borderColor: '#0063cc',
        fontFamily: [
            '-apple-system',
            'BlinkMacSystemFont',
            '"Segoe UI"',
            'Roboto',
            '"Helvetica Neue"',
            'Arial',
            'sans-serif',
            '"Apple Color Emoji"',
            '"Segoe UI Emoji"',
            '"Segoe UI Symbol"',
        ].join(','),
        '&:hover': {
            backgroundColor: '#0069d9',
            borderColor: '#0062cc',
            boxShadow: 'none',
        },
        '&:active': {
            boxShadow: 'none',
            backgroundColor: '#0062cc',
            borderColor: '#005cbf',
        },
        '&:focus': {
            boxShadow: '0 0 0 0.2rem rgba(0,123,255,.5)',
        },
    },
})(Button);

const ColorButton = withStyles((theme) => ({
    root: {
        color: theme.palette.getContrastText(purple[500]),
        backgroundColor: purple[500],
        '&:hover': {
            backgroundColor: purple[700],
        },
    },
}))(Button);

const useStyles = makeStyles((theme) => ({
    margin: {
        margin: theme.spacing(1),
    },
}));

const theme = createMuiTheme({
    palette: {
        primary: green,
    },
});

function PersonelNavigation() {
    const classes = useStyles();
    const iconFirst = require('./fromSVG.svg');
    const iconSecond = require('./displayIcon.svg');
    return (
        <div>
            <Col style={{
                display: "flex",
                flexDirection: "row",
                justifyContent: "column",
                flex: 2
            }}>
                <Row style={{
                    display: "flex",
                    flexDirection: "row",
                    flex: 2,
                    justifyContent: "center",
                    alignItems: "center",
                    margin: "25px"
                }}>
                    <Link to="PermissionFilling" style={{
                        display: "block",
                        alignItems: "center",
                        justifyContent: "center",
                        margin: "15px"
                    }}>
                        <Button variant="outlined" color="primary">
                                <img  style={{ height:"12vw",width:"12vw",margin:"10px"}} src={iconFirst}/>
                            İZİN TALEBİ YARAT
                        </Button>
                    </Link>
                    <Link to="PreviousPermissons" style={{
                        display: "block",
                        alignItems: "center",
                        justifyContent: "center",
                    }}>

                            <Button variant="outlined" color="secondary">
                                <img  style={{height:"12vw",width:"12vw",margin:"10px"}} src={iconSecond}/>
                                GEÇMİŞ İZİNLERİ GÖRÜNTÜLE
                            </Button>
                    </Link>
                </Row>
            </Col>
        </div>
    );
}



export default PersonelNavigation;
