import React from "react";
import {Link} from "react-router-dom";
import Grid from '@material-ui/core/Grid';
import makeStyles from "@material-ui/core/styles/makeStyles";


const useStyles = makeStyles((theme) => ({
    root: {
        flexGrow: 1,
    },
    paper: {
        padding: theme.spacing(2),
        textAlign: 'center',
        color: theme.palette.text.secondary,
    },
}));


function displayIndividualOperations(flag) {
    const crateDemandIcon = require('./SystemImages/FillingFormImage.svg');
    const displayPermissionDemandIcon = require('./SystemImages/DisplayPermissionButtonIcon.svg');
    if (flag) {
        return (

            <Grid container spacing={1}>
                <Grid item spacing={1} xs={12} sm={6} style={{justifyContent:"center"}}>
                    <button style={{display: "flex", flex: 0.5, marginRight: "10px",marginLeft: "10px", width: "100%"}}
                            type="button" className="btn btn-success">
                        <img alt="IconEye"
                             style={{display: "flex", flex:0.1,flexDirection: "left", width: "60px", height: "60px",margin:"5px"}}
                             src={crateDemandIcon}/>
                        <h1 style={{display: "flex",flex:0.4, flexDirection: "flex-start", fontSize: "16px",margin:"auto"}}>
                            İZİN TALEBİ YARAT
                        </h1>
                    </button>
                </Grid>
                <Grid item xs={12} sm={6} spacing={1} style={{justifyContent:"center"}}>
                    <button style={{display: "flex", flex: 0.5, marginRight: "10px",marginLeft: "10px", width: "100%"}}
                            type="button" className="btn btn-success">
                        <img alt="IconEye"
                             style={{display: "flex",flex:0.3, flexDirection: "left", width: "50px", height: "50px"}}
                             src={displayPermissionDemandIcon}/>
                        <h1 style={{display: "flex",flex:0.2,flexDirection: "flex-start", fontSize: "16px",margin:"auto"}}>
                            İZİNLERİ GÖRÜNTÜLE
                        </h1>
                    </button>
                </Grid>
            </Grid>
        )
    } else {
        return null;
    }
}


class NavigateTheChief extends React.Component {

    constructor() {
        super();
        this.state = {
            selectedIndividualOperations: false
        }
        this.handleOperationTypeSelection = this.handleOperationTypeSelection.bind(this);
    }

    handleOperationTypeSelection(event) {
        this.setState({
            selectedIndividualOperations: !this.state.selectedIndividualOperations,
        })

    }

    render() {
        const managementIcon = require('./SystemImages/ManagerIcon.svg');
        const individualIcon = require('./SystemImages/IndividualIcon.svg');


        return (
            <Grid container spacing={2} style={{width: "100%"}}>

                <Grid item xs={12} sm={6}>
                    <Link to="FillingThePermissionForm" style={{display: "flex", flex: 1, textDecoration: "none"}}>
                        <button type="button" className="btn btn-success"
                                style={{display: "flex", flex: 1, margin: "10px"}}>
                            <img alt="IconEye" style={{width: "150px", height: "150px", fill: "black"}}
                                 src={managementIcon}/>
                            <h3 style={{margin: "auto"}}>
                                İDARİ İŞLEMLERİM
                            </h3>
                        </button>
                    </Link>
                </Grid>

                <Grid item xs={12} sm={6} style={{display: "flex", flexDirection: "column", flex: 1}}>

                    <button type="button" className="btn btn-success" onClick={this.handleOperationTypeSelection}
                            style={{display: "flex", flex: 0.5, margin: "10px"}}>

                        <img style={{width: "130px", height: "130px"}} src={individualIcon} alt="IndividualOperations"/>
                        <h3 style={{margin: "auto"}}>
                            KİŞİSEL İŞLEMLERİM
                        </h3>
                    </button>
                    <Grid item xs={12} style={{display: "flex", flexDirection: "column", flex: 0.5,width:"100%"}}>
                        <Link to="PreviousPermissons"
                              style={{display: "flex", flex: 0.5, textDecoration: "none", justifyContent: "center",width:"100%"}}>
                            {displayIndividualOperations(this.state.selectedIndividualOperations)}
                        </Link>
                    </Grid>
                </Grid>

            </Grid>

        );
    }
}

export default NavigateTheChief;
