import React from "react";
import BootstrapSwitchButton from 'bootstrap-switch-button-react'

export function acceptionSymb() {
    return(
        <div style={{
            display:"flex",
            margin:"auto",
            flex:1,
            background:"rgb(39,180,64)",
            borderRadius:"4%"
        }}>
            <h1  style={{
                display:"flex",
                margin:"auto",
                fontSize:"20px"}}
            >
                ✔
            </h1>
        </div>
    )
}
export function deniedSymb() {
    return(
        <div style={{
            display:"flex",
            margin:"auto",
            flex:1,
            background:"rgb(203,27,27)",
            borderRadius:"4%"
        }}>
            <h1  style={{
                display:"flex",
                margin:"auto",
                fontSize:"20px"}}
            >
                ✖
            </h1>
        </div>
    )
}



export function prevPermission(props) {
    let izinTalepID=22;
    let izinTalepTarihi="22/22/2222";
    let izinBaşlangıcTarihi = "22/22/2222";
    let izinBitisTarihi = "22/22/2222";

    if (props) {
        return (
            <div style={{
                display:"flex",
                flexDirection:"column",
                justifyContent:"row",
                marginTop:"8px",
                border:"0.2px solid black",
                padding:"2px"
            }}>
                <label style={{
                    justifyContent:"left",
                    border:"0.5px solid black",
                    flex:7,
                    borderRadius:"2%",
                    display:"flex",
                    background:"rgba(7,133,180,0.04)"}}
                >
                    <h1 style={{
                        display:"flex",
                        flexDirection:"row",
                        flex:1,
                        fontSize:"12px",
                        margin:"2px",
                        justifyContent:"center"
                    }}>
                        {izinTalepID}
                    </h1>

                    <h1 style={{
                        display:"flex",
                        flexDirection:"row",
                        margin:"2px",
                        flex:1,
                        fontSize:"12px",
                        justifyContent:"center"
                    }}>
                        {izinTalepTarihi}
                    </h1>

                    <h1 style={{
                        display:"flex",
                        flexDirection:"row",
                        flex:1,
                        fontSize:"12px",
                        justifyContent:"center",
                        margin:"2px"
                    }}>
                        {izinBaşlangıcTarihi}
                    </h1>

                    <h1 style={{
                        display:"flex",
                        flexDirection:"row",
                        flex:1,
                        fontSize:"12px",
                        margin:"2px",
                        justifyContent:"center"
                    }}>
                        {izinBitisTarihi}
                    </h1>

                    <div style={{
                        display:"flex",
                        flexDirection:"row",
                        flex:1.5,
                        fontSize:"12px",
                        justifyContent:"center"
                    }}>
                            {acceptionSymb()}
                    </div>

                    <div style={{
                        display:"flex",
                        flexDirection:"row",
                        flex:1.5,
                        fontSize:"12px",
                        justifyContent:"center"
                    }}>
                            {deniedSymb()}
                    </div>
                </label>
            </div>
        )
    }
    return null;
}

class PreviousPermissons extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            displayActives: true,
        }
        this.ToggleButton = this.ToggleButton.bind(this);
    }

    ToggleButton() {
        this.setState((currentState) => ({
            displayActives: !this.state.displayActives
        }));
    }

    render() {
        return (
            <div style={{
                display: "flex",
                flexDirection: "column",
                flex: 6
            }}>
                <div style={{
                    display: "flex",
                    flexDirection: "row",
                    flex: 1,
                    justifyContent: "center",
                    margin: "25px"
                }}>
                    <BootstrapSwitchButton
                        style={{
                            display: "flex",
                            flex: 1
                        }}
                        checked={true}
                        onlabel='AKTİF TALEPLER'
                        offlabel='GEÇMİŞ TALEPLER'
                        width={350}
                        onChange={() => this.ToggleButton()}
                    />
                </div>
                <div style={{
                    display: "flex",
                    flexDirection: "row",
                    flex: 1,
                    justifyContent: "flex-end",
                    textAlign: "center"
                }}>
                    <h1
                        style={{
                            display: "flex",
                            flexDirection: "row",
                            justifyContent: "center",
                            flex: 0.15,
                            fontFamily: "Roboto",
                            fontSize: "15px",
                            textShadow: "0px 4px 4px rgba(0, 0, 0, 0.25)",
                            borderRight: "1px solid black",
                            textAlign: "center",
                            border: "0.2px solid black",
                            borderRadius: "3%",
                            margin: "2px",
                            paddingTop: "10px",
                            paddingBottom: "10px"
                        }}>
                        İZİN NO
                    </h1>
                    <h1
                        style={{
                            display: "flex",
                            flexDirection: "row",
                            justifyContent: "center",
                            flex: 0.15,
                            fontFamily: "Roboto",
                            fontSize: "15px",
                            textShadow: "0px 4px 4px rgba(0, 0, 0, 0.25)",
                            borderRight: "1px solid black",
                            textAlign: "center",
                            border: "0.2px solid black",
                            borderRadius: "3%",
                            margin: "2px",
                            paddingTop: "10px",
                            paddingBottom: "10px"
                        }}>
                        İZİN TALEP TARİHİ
                    </h1>
                    <h1
                        style={{
                            display: "flex",
                            flexDirection: "row",
                            justifyContent: "center",
                            flex: 0.15,
                            fontFamily: "Roboto",
                            fontSize: "15px",
                            textShadow: "0px 4px 4px rgba(0, 0, 0, 0.25)",
                            borderRight: "1px solid black",
                            textAlign: "center",
                            border: "0.2px solid black",
                            borderRadius: "3%",
                            margin: "2px",
                            paddingTop: "10px",
                            paddingBottom: "10px"
                        }}>
                        İZİN BAŞLANGIÇ
                    </h1>
                    <h1
                        style={{
                            display: "flex",
                            flexDirection: "row",
                            justifyContent: "center",
                            flex: 0.15,
                            fontFamily: "Roboto",
                            fontSize: "15px",
                            textShadow: "0px 4px 4px rgba(0, 0, 0, 0.25)",
                            borderRight: "0.2px solid black",
                            textAlign: "center",
                            border: "0.2px solid black",
                            borderRadius: "3%",
                            margin: "2px",
                            paddingTop: "10px",
                            paddingBottom: "10px"
                        }}>
                        İZİN BİTİŞ
                    </h1>
                    <h1
                        style={{
                            display: "flex",
                            flexDirection: "row",
                            justifyContent: "center",
                            flex: 0.2,
                            fontFamily: "Roboto",
                            fontSize: "15px",
                            textShadow: "0px 4px 4px rgba(0, 0, 0, 0.25)",
                            textAlign: "center",
                            border: "1px solid black",
                            borderRadius: "3%",
                            margin: "2px",
                            paddingTop: "10px",
                            paddingBottom: "10px"

                        }}>
                        AMİR ONAYI
                    </h1>
                    <h1
                        style={{
                            display: "flex",
                            flexDirection: "row",
                            justifyContent: "center",
                            flex: 0.2,
                            fontFamily: "Scada",
                            fontSize: "15px",
                            textShadow: "0px 4px 4px rgba(0, 0, 0, 0.25)",
                            textAlign: "center",
                            border: "1px solid black",
                            borderRadius: "3%",
                            margin: "2px",
                            paddingTop: "10px",
                            paddingBottom: "10px"
                        }}>
                        GENEL YÖNETİCİ ONAYI
                    </h1>
                </div>
                {prevPermission(this.state.displayActives)}
            </div>
        )
    }
}

export default PreviousPermissons;
