import React from "react";
import {Link} from "react-router-dom";
import Grid from '@material-ui/core/Grid';
import makeStyles from "@material-ui/core/styles/makeStyles";


const useStyles = makeStyles((theme) => ({
    root: {
        flexGrow: 1,
    },
    paper: {
        padding: theme.spacing(2),
        textAlign: 'center',
        color: theme.palette.text.secondary,
    },
}));


function NavigateTheChief() {

    const iconFirst = require('./SystemImages/ManagerIcon.svg');
    const iconSecond = require('./SystemImages/IndividualIcon.svg');
    return (
        <Grid container spacing={2} style={{width:"100%"}}>
            <Grid item xs={12} sm={6} >
                <Link to="FillingThePermissionForm" style={{display:"flex",flex:1,textDecoration:"none"}} >
                    <button type="button" className="btn btn-success" style={{display:"flex",flex:1,margin:"20px"}}
                    >
                        <img  alt="IconEye" style={{width:"150px",height:"150px",fill:"black"}}
                              src={iconFirst} />
                        <h3 style={{margin:"auto"}}>
                            İDARİ İŞLEMLERİM
                        </h3>
                    </button>
                </Link>
            </Grid>
            <Grid item xs={12} sm={6} style={{display:"flex",flex:1}}>
                <Link to="PreviousPermissons" style={{display:"flex",flex:1,textDecoration:"none"}} >
                    <button type="button" className="btn btn-success" style={{display:"flex",flex:1,margin:"20px"}}  >
                        <img style={{width:"130px",height:"130px"}} src={iconSecond} alt="IconEye"/>
                        <h3 style={{margin:"auto"}}>
                            KİŞİSEL İŞLEMLERİM
                        </h3>
                    </button>
                </Link>

            </Grid>
        </Grid>

    );
}
export default NavigateTheChief;
