import React from 'react';
import {makeStyles} from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TablePagination from '@material-ui/core/TablePagination';
import TableRow from '@material-ui/core/TableRow';
import Button from "@material-ui/core/Button";
import {createMuiTheme, ThemeProvider} from '@material-ui/core/styles';
import {trTR} from '@material-ui/core/locale';
import {Bounce} from "react-reveal";
import Grid from "@material-ui/core/Grid";
import {Link} from "react-router-dom";
import ResponsiveDialog from "./ResponsiveDialog";


{/*Here the main theme that specifies the local and background color.*/
}
const theme = createMuiTheme({
    palette: {
        primary: {main: '#1976d2'}
    },

}, trTR);

{/*Here the columns that are specifies the main tags of rows.*/
}
const columns = [
    {
        id: 'permissionID',
        label: 'İzin ID'
    },
    {
        id: 'beginDateOfPermission',
        label: 'İZİN BAŞLANGIÇ',
    },
    {
        id: 'endDateOfPermission',
        label: 'İZİN BİTİŞ',
    },
    {
        id: 'chiefConfirmStatus',
        label: 'AMİR ONAYI',
    },
    {
        id: 'generalManagerConfirmStatus',
        label: 'GENEL MÜDÜR ONAYI',
    },
    /* {
         id: 'displayScreen',
         label: ' ÖNİZLEME',
     },*/
];

/* This function determines a button to monitor related permission in form*/
function displayPermissionForm() {
    return (
        <div style={{
            display: "flex",
            justifyContent: "flex-start"
        }}>
            <Link to="DisplayPermissionForm">
                <Button variant="outlined" color="primary" style={{
                    display: "flex",
                    justifyContent: "flex-start",
                    padding: "1px",
                    background: "rgba(180,133,205,0.64)",
                    borderRadius: "50%"
                }}>
                    <img style={{width: "30px", height: "20px", margin: "auto"}}
                         src={require('./SystemImages/DisplayPermissionButtonIcon.svg')}
                         alt="my image"/>
                </Button>
            </Link>
        </div>
    )
}

{/*Here the style definition for usage of varible classes*/
}

{/*
    This function determines StickyHeadTable to display
    related informations.
*/
}
export default class StickyHeadTable extends React.Component {
    /*
    * Here the variables and states that are determined to decide style.
    * */

    constructor(props) {
        super(props);
        this.defineRootAndContainerProperties = makeStyles({
            root: {
                width: '100%',
            },
            container: {
                width: "100%",
            },
        });
        /*This const determines styles for bounce which displays buttons as animated */
        this.stylesForBounce = makeStyles((theme) => ({
            root: {
                flexGrow: 1,
            },
            paper: {
                textAlign: 'center',
                padding: "10px",
                background: "rgba(139,133,133,0.21)"
            },
        }));

        this.state = {
            page: 0,
            rowsPerPage: 10,
            defineStyleForBounce: stylesForBounce(),
        }
        this.handleChangePage = this.handleChangePage.bind(this);
        this.handleChangeRowsPerPage = this.handleChangeRowsPerPage.bind(this);
        this.DisplayTheRowOfForm = this.DisplayTheRowOfForm.bind(this);
    }

    /*
        This method created for page change in StickyHeadTable
     */
    handleChangePage(event, newPage) {
        this.setState({
            page: newPage
        })
    };

    /*
    This method created for row per page change in StickyHeadTable
     */
    handleChangeRowsPerPage(event) {
        this.setState({
            rowsPerPage: +event.target.value,
            page: 0
        })
    };

    DisplayTheRowOfForm() {
        return (
            <React.Fragment>
                {/* First Grid defined here */}
                <Grid item xs={3}>
                    <Paper className={this.state.defineStyleForBounce.paper}>
                        <h1 style={{
                            fontSize: "20px",
                            borderRadius: "50%",
                            backgroundColor: "rgba(49,236,9,0.95)",
                            textAlign: "center"
                        }}>
                            ✔
                        </h1>
                        <h1 style={{
                            fontSize: "12px",
                            fontWeight: "bold",
                            textAlign: "center"
                        }}
                        >
                            ONAY
                        </h1>
                    </Paper>
                </Grid>
                <Grid item xs={4}>
                    <Paper className={this.state.defineStyleForBounce.paper}>

                        <h1 style={{
                            fontSize: "20px",
                            borderRadius: "50%",
                            backgroundColor: "rgba(255,220,0,0.95)",
                            textAlign: "center",
                            marginRight: "10px",
                            marginLeft: "10px"
                        }}>
                            ⊖
                        </h1>
                        <h1 style={{fontSize: "12px", textAlign: "center", fontWeight: "bold"}}
                        >
                            BEKLEMEDE
                        </h1>
                    </Paper>
                </Grid>
                <Grid item xs={2}>
                    <Paper className={this.state.defineStyleForBounce.paper}>
                        <h1 style={{
                            fontSize: "20px",
                            borderRadius: "50%",
                            backgroundColor: "rgba(220,16,16,0.95)",
                            textAlign: "center"
                        }}>
                            ✖
                        </h1>
                        <h1 style={{
                            fontSize: "12px",
                            fontWeight: "bold",
                            textAlign: "center"
                        }}
                        >
                            RED
                        </h1>
                    </Paper>
                </Grid>
                <Grid item xs={3}>
                    <Paper className={this.state.defineStyleForBounce.paper}>
                        <h1 style={{
                            fontSize: "20px",
                            borderRadius: "50%",
                            backgroundColor: "rgb(253,131,1)",
                            textAlign: "center"
                        }}>
                            !
                        </h1>
                        <h1 style={{
                            fontSize: "12px",
                            fontWeight: "bold",
                            textAlign: "center"
                        }}
                        >
                            REVİZE
                        </h1>

                    </Paper>
                </Grid>
            </React.Fragment>
        );
    }

    render() {
        return (
            <div>
                <div style={{display: "flex", justifyContent: "flex-end", margin: "5px"}}>
                    <Bounce>
                        <Grid container item xs={12} spacing={1}>
                            {this.DisplayTheRowOfForm}
                        </Grid>
                    </Bounce>
                </div>
                <ThemeProvider theme={theme}>
                    <Paper className={this.state.defineRootAndContainerProperties.root}>
                        <TableContainer className={this.state.defineRootAndContainerProperties.container}>
                            <Table stickyHeader aria-label="sticky table">
                                <TableHead>
                                    <TableRow>
                                        {columns.map((column) => (
                                            <TableCell
                                                key={column.id}
                                            >
                                                {column.label}
                                            </TableCell>
                                        ))}
                                    </TableRow>
                                </TableHead>
                                <TableBody>
                                    {this.props.rows.slice(this.state.page * this.state.rowsPerPage, this.state.page * this.state.rowsPerPage + this.state.rowsPerPage).map((row) => {
                                        console.log("sdads")
                                        return (
                                            <TableRow hover role="checkbox" tabIndex={-1} key={row.permissionID}>
                                                {columns.map((column) => {
                                                    const value = row[column.id];
                                                    return (
                                                        <TableCell key={column.id}>
                                                            {value}
                                                            {column.id === "chiefAcceptance" &&
                                                            <ResponsiveDialog chiefStatus={row.chiefConfirmStatus}
                                                                              explanationOfChief={row.chiefsDescription}/>}
                                                            {column.id === "generalManAcceptance" && <ResponsiveDialog
                                                                chiefStatus={row.generalManagerConfirmStatus}
                                                                explanationOfGeneralManager={row.generalManagerDescription}/>}
                                                            {column.id === "displayScreen" && displayPermissionForm(row.userStatus)}
                                                        </TableCell>
                                                    );
                                                })}
                                            </TableRow>
                                        );
                                    })}
                                </TableBody>
                            </Table>
                        </TableContainer>
                        <TablePagination
                            rowsPerPageOptions={[10, 25, 100]}
                            component="div"
                            count={this.props.rows.length}
                            rowsPerPage={this.state.rowsPerPage}
                            page={this.state.page}
                            onChangePage={this.handleChangePage}
                            onChangeRowsPerPage={this.handleChangeRowsPerPage}
                        />
                    </Paper>
                </ThemeProvider>
            </div>
        );
    }
}
