import React from "react";
import Logo from "./SystemImages/CompanyLogo.png";
import {Link} from "react-router-dom";
import {Row} from "react-bootstrap";
import Col from "react-bootstrap/Col";
import {connect} from "react-redux";

const axios = require('axios');

const api = axios.create({
    baseURL: `http://localhost:4000`
})

/*function getManagersNameAndSignaturebyID(givenIDofManager) {
    api.get('/getNameAndSignatureByID/' + givenIDofManager)
        .then(
            function (response) {
                console.log(response)
                return response;
            }
        )


}*/

const mapStateToProps = (state) => {
    return {
        userID: state.permissionReducer.userID,

        userStatus: state.permissionReducer.userStatus,
        displayStatus: state.permissionReducer.displayStatus,

        personalName: state.userLoginReducer.personalName,
        chiefsName: state.userLoginReducer.chiefsName,
        generalManagersName: state.userLoginReducer.generalManagersName,


        demandDateOfPermission: state.permissionReducer.demandDateOfPermission,

        beginDateOfPermission: state.permissionReducer.beginDateOfPermission,
        endDateOfPermission: state.permissionReducer.endDateOfPermission,

        foldCode: state.permissionReducer.foldCode,
        areaCode: state.permissionReducer.areaCode,

        selectVehicleUsageName: state.permissionReducer.selectVehicleUsageName,
        selectVehicleUsageID: state.permissionReducer.selectVehicleUsageID,

        permissionDescription: state.permissionReducer.permissionDescription,

        personalCarUsage: state.permissionReducer.personalCarUsage,
        totalDistanceOfIndividualCar: state.permissionReducer.totalDistanceOfIndividualCar,
        priceOfTrainOrBus: state.permissionReducer.priceOfTrainOrBus,

        displayThePermissionName: state.permissionReducer.displayThePermissionName,
        setPermissionType: state.permissionReducer.setPermissionType

    }
};


class DisplayPermissionForm extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            data:undefined
        }


    }

    componentDidMount() {
        this.getData().then((data) => {
            this.setState({data: data})
        });
    }

    /*
       this.setState=({
                                userStatus: response.data.usersPermission.userStatus,
                                displayStatus: response.data.usersPermission.displayStatus,
                                isPermissionActive: response.data.usersPermission.isPermissionActive,
                                personalName: response.data.usersPermission.personalName,
                                demandDateOfPermission: response.data.usersPermission.demandDateOfPermission,

                                beginDateOfPermission: response.data.usersPermission.beginDateOfPermission,
                                endDateOfPermission: response.data.usersPermission.endDateOfPermission,

                                begDateSelectionStat: response.data.usersPermission.begDateSelectionStat,
                                endDateSelectionStat: response.data.usersPermission.endDateSelectionStat,

                                foldCode: response.data.usersPermission.foldCode,
                                areaCode: response.data.usersPermission.areaCode,

                                permissionDescription: response.data.usersPermission.permissionDescription,

                                selectVehicleUsageName: response.data.usersPermission.selectVehicleUsageName,
                                selectVehicleUsageID: response.data.usersPermission.selectVehicleUsageID,

                                priceOfTrainOrBus: response.data.usersPermission.priceOfTrainOrBus,
                                totalDistanceOfIndividualCar: response.data.usersPermission.totalDistanceOfIndividualCar,

                                displayThePermissionName: response.data.usersPermission.displayThePermissionName,
                                setThePermissionType: response.data.usersPermission.setThePermissionType,

                                chiefConfirmStatus: response.data.usersPermission.chiefConfirmStatus,
                                chiefsDescription: response.data.usersPermission.chiefsDescription,

                                generalManagerConfirmStatus: response.data.usersPermission.generalManagerConfirmStatus,
                                generalManagerDescription: response.data.usersPermission.generalManagerDescription,
                            })
                        })

     */
    getData() {
        let endCode = window.location.href.split("/")[window.location.href.split("/").length - 1];
        if (endCode !== "DisplayPermissionForm") {
           return ( api.get('/DisplayPermissionForm/' + endCode)
                .then(
                    function (response) {
                        console.log(response.data.usersPermission,"diyorrrrr")
                        return response.data.usersPermission
                    })
           )
        }
    }


    render() {

        return (
            <div style={{
                display: "flex",
                flexDirection: "column",
                flex: 10
            }}>
                <div style={{
                    display: "flex",
                    flexDirection: "column",
                    flex: 9,
                    border: "0.2px solid black"
                }}>
                    <div
                        style={{
                            display: "flex",
                            flex: 1,
                            marginTop: "4px",
                            marginBottom: "4px",
                            border: "0.6px solid black"
                        }}>
                        {displayLogoAndMainDescription(this.state.areaCode, this.state.foldCode)}
                    </div>

                    <div style={{
                        display: "flex",
                        flex: 1,
                        marginTop: "4px",
                        marginBottom: "4px",
                        border: "0.6px solid black"
                    }}>
                        {displayPermissionTypeAndRest(this.state.setPermissionType)}
                    </div>
                    <div style={{
                        display: "flex",
                        flex: 1,
                        marginTop: "4px",
                        marginBottom: "4px",
                        border: "0.6px solid black"
                    }}>
                        {displayPersonalInformationPart(this.state.data.personalName,new Date(Date.now()))}
                    </div>
                    <div style={{
                        display: "flex",
                        flex: 1,
                        marginTop: "4px",
                        marginBottom: "4px",
                        border: "0.6px solid black"
                    }}>

                        {

                            displayEmployeesPermissionDates(
                                new Date(
                                    Date.now()
                                ),
                                new Date(
                                    Date.now()
                                )
                            )}
                    </div>
                    <div style={{
                        display: "flex",
                        flex: 1,
                        marginTop: "4px",
                        marginBottom: "4px",
                        border: "0.6px solid black"
                    }}>
                        {displayVehicleUsagesOfPersonel(this.state.selectVehicleUsageID)}
                    </div>
                    <div style={{
                        display: "flex",
                        flex: 1,
                        marginTop: "4px",
                        marginBottom: "4px",
                        border: "0.6px solid black"
                    }}>
                        {displayTheExplanationOfPermission(this.state.permissionDescription)}
                    </div>
                    <div style={{
                        display: "flex",
                        flex: 1,
                        marginTop: "4px",
                        marginBottom: "4px",
                        border: "0.6px solid black"
                    }}>
                        {displayDetailsOfVehicleUsage(this.state.totalDistanceOfIndividualCar, this.state.priceOfTrainOrBus, this.state.selectVehicleUsageID)}
                    </div>
                    <div style={{
                        display: "flex",
                        flex: 1,
                        marginTop: "4px",
                        marginBottom: "4px",
                        border: "0.6px solid black"
                    }}>
                        {displayTheAcceptionPart(this.state.personalName, this.state.personalName, this.state.personalName)}
                    </div>

                    <div style={{
                        display: "flex",
                        flex: 1,
                        marginTop: "4px",
                        marginBottom: "4px",
                        border: "0.6px solid black"
                    }}>
                        {displayFormHRrelatedInfo()}
                    </div>
                </div>
                <div style={{
                    display: "flex",
                    flex: 1,
                    marginTop: "4px",
                    marginBottom: "4px"
                }}>

                    {this.state.displayStatus === 2 && (this.state.userStatus === 1 ? displayManagersButtonsForForm() : displayPersonelsButtonsForForm(this.props,
                        this.state.userID,
                        this.state.userStatus,
                        this.state.personalName,

                        this.state.demandDateOfPermission,
                        this.state.beginDateOfPermission,
                        this.state.endDateOfPermission,

                        this.state.foldCode,
                        this.state.areaCode,
                        this.state.selectVehicleUsageName,
                        this.state.selectVehicleUsageID,
                        this.state.permissionDescription,
                        this.state.personalCarUsage,
                        this.state.totalDistanceOfIndividualCar,
                        this.state.priceOfTrainOrBus
                    ))}

                </div>

            </div>
        )
    }
}

//ÜST BAŞLIĞIN OLDUĞU KOMPONENT ( __1__ )
function displayLogoAndMainDescription(areaCode, foldNumb) {
    return (
        <div style={{
            display: "flex",
            flexDirection: "row",
            flex: 5,
            border: "0.1px solid black"
        }}>
            <img src={Logo} alt="Logo" style={{
                display: "flex",
                flex: 1,
                width: "200px",
                height: "100px",
                margin: "0.1vw",
                border: "0.1px solid black",
                borderRadius: "2%"
            }}/>
            <div style={{
                display: "flex",
                flexDirection: "column",
                flex: 4,

            }}>
                <div style={{
                    display: "flex",
                    flex: 2,
                    justifyContent: "flex-start",
                    border: "0.1px solid black",
                    margin: "0.1vw"
                }}>
                    <h1 style={{
                        display: "flex",
                        flex: 1.8,
                        justifyContent: "center",
                        fontSize: "3vw",
                        fontWeight: "normal",
                        margin: "auto",
                        fontFamily: "Scada"
                    }}>
                        DESIRD TASARIM
                    </h1>

                    <h1 style={{
                        display: "flex",
                        flex: 0.2,
                        fontSize: "2.5vw",
                        justifyContent: "center",
                        margin: "auto",
                        fontFamily: "Scada"
                    }}>
                        {areaCode}
                    </h1>
                </div>

                <div style={{
                    display: "flex",
                    flex: 2,
                    justifyContent: "flex-start",
                    border: "0.1px solid black",
                    margin: "0.1vw"
                }}>
                    <h1 style={{
                        display: "flex",
                        flex: 1.9,
                        textAlign: "center",
                        justifyContent: "center",
                        fontSize: "2.5vw",
                        fontWeight: "normal",
                        margin: "auto",
                        fontFamily: "Scada"
                    }}>
                        PERSONEL İZİN BELGESİ FORMU
                    </h1>

                    <h1 style={{
                        display: "flex",
                        flex: 0.1,
                        fontSize: "2.5vw",
                        justifyContent: "center",
                        margin: "auto",
                        border: "0.5px solid black",
                        fontFamily: "Scada"
                    }}>
                        {foldNumb}
                    </h1>
                </div>
            </div>
        </div>
