import React from "react";
import {Link} from "react-router-dom";
import {Row} from "react-bootstrap";
import Grid from '@material-ui/core/Grid';
import Col from "react-bootstrap/Col";
import ThemeProvider from "react-bootstrap/ThemeProvider";
import Button from "@material-ui/core/Button";
import createMuiTheme from "@material-ui/core/styles/createMuiTheme";
import makeStyles from "@material-ui/core/styles/makeStyles";
import green from "@material-ui/core/colors/green";
import withStyles from "@material-ui/core/styles/withStyles";
import Paper from "@material-ui/core/Paper";

const useStyles = makeStyles((theme) => ({
    root: {
        flexGrow: 1,
    },
    paper: {
        padding: theme.spacing(2),
        textAlign: 'center',
        color: theme.palette.text.secondary,
    },
}));




function PersonelNavigation() {

    const iconFirst = require('./fromSVG.svg');
    const iconSecond = require('./displayIcon.svg');
    const classes = useStyles();
    return (

        <Grid container spacing={3}>

            <Grid item xs={6}>
                <Link to="PermissionFilling">
                    <button type="button" className="btn btn-success"
                          >
                        <Paper className={classes.paper}>
                            <img
                                src={iconFirst}/>
                            <h3 style={{
                                display: "flex",
                                justifyContent: "center",
                                margin: "auto",
                                fontSize: "2.5vw",
                                flex: 0.5
                            }}>
                                İZİN TALEBİ YARAT
                            </h3>

                        </Paper>

                    </button>
                </Link>
            </Grid>

            <Grid item xs={6}>
                <Link to="PreviousPermissons">
                    <button type="button" className="btn btn-success">
                        <img src={iconSecond}/>
                        <h3 style={{
                            margin: "auto",
                            fontSize: "2.5vw"
                        }}>
                            GEÇMİŞ İZİNLERİ GÖRÜNTÜLE
                        </h3>
                    </button>
                </Link>
            </Grid>
        </Grid>

    );
}


export default PersonelNavigation;
