import React from "react";
import Logo from "./logok.png";
import {Link} from "react-router-dom";


function PerPerDemScreen() {
    return (
        <div style={{
            display: "flex",
            flexDirection: "column",
            flex: 8
        }}>
            <div
                style={{
                    display: "flex",
                    flex: 1,
                    marginTop:"4px",
                    marginBottom:"4px"
                }}>
                {headPart()}
            </div>

            <div style={{
                display: "flex",
                flex: 1,
                marginTop:"4px",
                marginBottom:"4px"
            }}>
                {underHeadPart()}
            </div>
            <div style={{
                display: "flex",
                flex: 1,
                marginTop:"4px",
                marginBottom:"4px"
            }}>
                {personelInfoPart()}
            </div>
            <div style={{
                display: "flex",
                flex: 1,
                marginTop:"4px",
                marginBottom:"4px"
            }}>
                {personelDatesAndExp()}
            </div>
            <div style={{
                display: "flex",
                flex: 1,
                marginTop:"4px",
                marginBottom:"4px"
            }}>
                {vhcUsageOfPers()}
            </div>
            <div style={{
                display: "flex",
                flex: 1,
                marginTop:"4px",
                marginBottom:"4px"
            }}>
                {perExplPart()}
            </div>
            <div style={{
                display: "flex",
                flex: 1,
                marginTop:"4px",
                marginBottom:"4px"
            }}>
                {vhclUsgInfo()}
            </div>
            <div style={{
                display: "flex",
                flex: 1,
                marginTop:"4px",
                marginBottom:"4px"
            }}>
                {perAccpPart()}
            </div>

        </div>
    )
}

//ÜSTTEN 2.BLOK KOMPONENT İZİN TÜRÜ ALINIR, BİR KOMPONENTİ DE İÇERDE YARATIR.
function underHeadPart() {
    return (
        <div style={{
            display: "flex",
            flexDirection: "row",
            flex:6
        }}>
            <div style={{
                display: "flex",
                flex:1,
                justifyContent:"center",
                border: "0.5px solid black",
                borderRadius: "2%",
                textAlign: "center"
            }}>
                <h1 style={{
                    display:"flex",
                    flex:1,
                    fontFamily:"Scada",
                    fontSize:"22px",
                    margin:"auto",
                    justifyContent:"center"
                }}>
                    İZİN TÜRÜ
                </h1>

            </div>
            <div style={{
                display: "flex",
                flex:5
            }}>
                {perAccCompt()}
            </div>
        </div>
    );
}

//İZİN TİPİ SEÇME COMPONENTİ
function perAccCompt() {
    return (
        <div style={{
            display: "flex",
            flexDirection: "column",
            flex: 6,
            border: "0.2px solid black"
        }}>
            <div style={{
                display: "flex",
                flexDirection: "row",
                flex: 3
            }}>
                <div style={{
                    display: "flex",
                    flexDirection: "row",
                    flex: 1,
                    borderRight: "0.1px solid black",
                    borderBottom: "0.1px solid black"
                }}>
                    <h1 style={{
                        display: "flex",
                        justifyContent: "center",
                        flex: 0.8,
                        fontFamily:"Scada",
                        fontSize: "18px",
                        margin:"auto"
                    }}>
                        Yıllık İzin
                    </h1>

                    <input type="checkbox" style={{
                        margin:"auto",
                        display: "flex",
                        flex: 0.2,
                        height:"80%",
                    }}/>
                </div>

                <div style={{
                    display: "flex",
                    flexDirection: "row",
                    flex: 1,
                    borderRight: "0.1px solid black",
                    borderBottom: "0.1px solid black"
                }}>
                    <h1 style={{
                        display: "flex",
                        flex: 0.8,
                        justifyContent: "center",
                        fontFamily:"Scada",
                        fontSize: "18px",
                        margin:"auto"
                    }}>
                        Görevli
                    </h1>

                    <input type="checkbox" style={{
                        display: "flex",
                        flex: 0.2,
                        height:"80%",
                        margin:"auto"
                    }}/>
                </div>
                <div style={{
                    display: "flex",
                    flexDirection: "row",
                    flex: 1,
                    borderBottom: "0.1px solid black"
                }}>
                    <h1 style={{
                        display: "flex",
                        flex: 0.8,
                        justifyContent: "center",
                        fontFamily:"Scada",
                        fontSize: "18px",
                        margin:"auto"
                    }}>
                        Diğer
                    </h1>
                    <input type="checkbox" style={{
                        display: "flex",
                        flex: 0.2,
                        height:"80%",
                        margin:"auto"
                    }}/>
                </div>
            </div>
            <div style= {{
                display: "flex",
                flexDirection: "row",
                flex: 3
            }}>
                <div style={{
                    display: "flex",
                    flexDirection: "row",
                    flex: 1,
                    borderRight:"0.1px solid black"
                }}>
                    <h1 style={{
                        display: "flex",
                        justifyContent: "center",
                        flex: 0.8,
                        fontSize: "14px",
                        margin:"auto",
                        fontFamily:"Scada"
                    }}>
                        Ücretsiz İzin
                    </h1>

                    <input type="checkbox" style={{
