import React from "react";
import Logo from "./SystemImages/CompanyLogo.png";
import {Link} from "react-router-dom";
import {Row} from "react-bootstrap";
import Col from "react-bootstrap/Col";
import {connect} from "react-redux";
import moment from "moment";

const axios = require('axios');

const api = axios.create({
    baseURL: `http://localhost:4000`
})

/*function getManagersNameAndSignaturebyID(givenIDofManager) {
    api.get('/getNameAndSignatureByID/' + givenIDofManager)
        .then(
            function (response) {
                console.log(response)
                return response;
            }
        )


}*/

const mapStateToProps = (state) => {
    return {
        userID: state.permissionReducer.userID,

        userStatus: state.permissionReducer.userStatus,
        displayStatus: state.permissionReducer.displayStatus,

        personalName: state.userLoginReducer.personalName,
        chiefsName:state.userLoginReducer.chiefsName,
        generalManagersName:state.userLoginReducer.generalManagersName,


        demandDateOfPermission:moment().format("DD-MM-YYYY HH:mm:ss"),

        beginDateOfPermission: state.permissionReducer.beginDateOfPermission,
        endDateOfPermission: state.permissionReducer.endDateOfPermission,

        foldCode: state.permissionReducer.foldCode,
        areaCode: state.permissionReducer.areaCode,

        selectVehicleUsageName: state.permissionReducer.selectVehicleUsageName,
        selectVehicleUsageID: state.permissionReducer.selectVehicleUsageID,

        permissionDescription: state.permissionReducer.permissionDescription,

        personalCarUsage: state.permissionReducer.personalCarUsage,
        totalDistanceOfIndividualCar: state.permissionReducer.totalDistanceOfIndividualCar,
        priceOfTrainOrBus: state.permissionReducer.priceOfTrainOrBus,

        displayThePermissionName: state.permissionReducer.displayThePermissionName,
        setPermissionType: state.permissionReducer.setPermissionType

    }
};


class DisplayPermissionForm extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            sendPermissionDemand: false,
        }

    }

    componentDidMount() {
        if (this.state.sendPermissionDemand) {
            console.log("Gönderilcek")
        }
        let endCode = window.location.href.split("/")[window.location.href.split("/").length-1];
        if(endCode!== undefined && endCode!== null){
            api.get('/DisplayPermissionForm/' +endCode,
            ).then(res => {


                this.setState({

                    userStatus: res.userPermission.userStatus,
                    displayStatus:res.userPermission.displayStatus,

                    personalName:res.userPermission.personalName,
                    chiefsName:res.userPermission.chiefsName,
                    generalManagersName:res.userPermission.generalManagersName,


                    demandDateOfPermission:res.userPermission.demandDateOfPermission,

                    beginDateOfPermission: res.userPermission.beginDateOfPermission,
                    endDateOfPermission: res.userPermission.endDateOfPermission,

                    foldCode: res.userPermission.foldCode,
                    areaCode: res.userPermission.areaCode,

                    selectVehicleUsageName: res.userPermission.selectVehicleUsageName,
                    selectVehicleUsageID: res.userPermission.selectVehicleUsageID,

                    permissionDescription:res.userPermission.permissionDescription,

                    personalCarUsage:res.userPermission.personalCarUsage,
                    totalDistanceOfIndividualCar: res.userPermission.totalDistanceOfIndividualCar,
                    priceOfTrainOrBus:res.userPermission.priceOfTrainOrBus,

                    displayThePermissionName:res.userPermission.displayThePermissionName,
                    setPermissionType: res.userPermission.setPermissionType
                })

                }
            )
        }


    }

    render() {

        return (
            <div style={{
                display: "flex",
                flexDirection: "column",
                flex: 10
            }}>
                <div style={{
                    display: "flex",
                    flexDirection: "column",
                    flex: 9,
                    border: "0.2px solid black"
                }}>
                    <div
                        style={{
                            display: "flex",
                            flex: 1,
                            marginTop: "4px",
                            marginBottom: "4px",
                            border: "0.6px solid black"
                        }}>
                        {displayLogoAndMainDescription(this.props.areaCode, this.props.foldCode)}
                    </div>

                    <div style={{
                        display: "flex",
                        flex: 1,
                        marginTop: "4px",
                        marginBottom: "4px",
                        border: "0.6px solid black"
                    }}>
                        {displayPermissionTypeAndRest(this.props.setPermissionType)}
                    </div>
                    <div style={{
                        display: "flex",
                        flex: 1,
                        marginTop: "4px",
                        marginBottom: "4px",
                        border: "0.6px solid black"
                    }}>
                        {displayPersonalInformationPart(this.props.personalName, this.props.demandDateOfPermission)}
                    </div>
                    <div style={{
                        display: "flex",
                        flex: 1,
                        marginTop: "4px",
                        marginBottom: "4px",
                        border: "0.6px solid black"
                    }}>
                        {

                            displayEmployeesPermissionDates(
                                this.props.beginDateOfPermission.getDate() + "/" +
                                (this.props.beginDateOfPermission.getMonth() + 1) + "/" +
                                this.props.beginDateOfPermission.getFullYear()
                                ,
                                this.props.beginDateOfPermission.getHours() +
                                " " +
                                this.props.beginDateOfPermission.getMinutes(),

                                this.props.endDateOfPermission.getDate() + "/" +
                                (this.props.endDateOfPermission.getMonth() + 1) + "/" +
                                this.props.endDateOfPermission.getFullYear(),

                                this.props.endDateOfPermission.getHours() +
                                " " +
                                this.props.endDateOfPermission.getMinutes(), this.props.beginDateOfPermission,
                                this.props.endDateOfPermission
                            )}
                    </div>
                    <div style={{
                        display: "flex",
                        flex: 1,
                        marginTop: "4px",
                        marginBottom: "4px",
                        border: "0.6px solid black"
                    }}>
                        {displayVehicleUsagesOfPersonel(this.props.selectVehicleUsageID)}
                    </div>
                    <div style={{
                        display: "flex",
                        flex: 1,
                        marginTop: "4px",
                        marginBottom: "4px",
                        border: "0.6px solid black"
                    }}>
                        {displayTheExplanationOfPermission(this.props.permissionDescription)}
                    </div>
                    <div style={{
                        display: "flex",
                        flex: 1,
                        marginTop: "4px",
                        marginBottom: "4px",
                        border: "0.6px solid black"
                    }}>
                        {displayDetailsOfVehicleUsage(this.props.totalDistanceOfIndividualCar, this.props.priceOfTrainOrBus, this.props.selectVehicleUsageID)}
                    </div>
                    <div style={{
                        display: "flex",
                        flex: 1,
                        marginTop: "4px",
                        marginBottom: "4px",
                        border: "0.6px solid black"
                    }}>
                        {displayTheAcceptionPart(this.props.personalName,this.props.personalName,this.props.personalName)}
                    </div>

                    <div style={{
                        display: "flex",
                        flex: 1,
                        marginTop: "4px",
                        marginBottom: "4px",
                        border: "0.6px solid black"
                    }}>
                        {displayFormHRrelatedInfo()}
                    </div>
                </div>
                <div style={{
                    display: "flex",
                    flex: 1,
                    marginTop: "4px",
                    marginBottom: "4px"
                }}>

                    {this.props.displayStatus === 2 && (this.props.userStatus === 1 ? displayManagersButtonsForForm() : displayPersonelsButtonsForForm(this.props,
                        this.props.userID,
                        this.props.userStatus,
                        this.props.personalName,
                        this.props.demandDateOfPermission,
                        this.props.beginDateOfPermission,
                        this.props.endDateOfPermission,
                        this.props.foldCode,
                        this.props.areaCode,
                        this.props.selectVehicleUsageName,
                        this.props.selectVehicleUsageID,
                        this.props.permissionDescription,
                        this.props.personalCarUsage,
                        this.props.totalDistanceOfIndividualCar,
                        this.props.priceOfTrainOrBus
                    ))}

                </div>

            </div>
        )
    }
}

//ÜST BAŞLIĞIN OLDUĞU KOMPONENT ( __1__ )
function displayLogoAndMainDescription(areaCode, foldNumb) {
    return (
        <div style={{
            display: "flex",
            flexDirection: "row",
            flex: 5,
            border: "0.1px solid black"
        }}>
            <img src={Logo} alt="Logo" style={{
                display: "flex",
                flex: 1,
                width: "200px",
                height: "100px",
                margin: "0.1vw",
                border: "0.1px solid black",
                borderRadius: "2%"
            }}/>
            <div style={{
                display: "flex",
                flexDirection: "column",
                flex: 4,

            }}>
                <div style={{
                    display: "flex",
                    flex: 2,
                    justifyContent: "flex-start",
                    border: "0.1px solid black",
                    margin: "0.1vw"
                }}>
                    <h1 style={{
                        display: "flex",
                        flex: 1.8,
                        justifyContent: "center",
                        fontSize: "3vw",
                        fontWeight: "normal",
                        margin: "auto",
                        fontFamily: "Scada"
                    }}>
                        DESIRD TASARIM
                    </h1>

                    <h1 style={{
                        display: "flex",
                        flex: 0.2,
                        fontSize: "2.5vw",
                        justifyContent: "center",
                        margin: "auto",
                        fontFamily: "Scada"
                    }}>
                        {areaCode}
                    </h1>
                </div>

                <div style={{
                    display: "flex",
                    flex: 2,
                    justifyContent: "flex-start",
                    border: "0.1px solid black",
                    margin: "0.1vw"
                }}>
                    <h1 style={{
                        display: "flex",
                        flex: 1.9,
                        textAlign: "center",
                        justifyContent: "center",
                        fontSize: "2.5vw",
                        fontWeight: "normal",
                        margin: "auto",
                        fontFamily: "Scada"
                    }}>
                        PERSONEL İZİN BELGESİ FORMU
                    </h1>

                    <h1 style={{
                        display: "flex",
                        flex: 0.1,
                        fontSize: "2.5vw",
                        justifyContent: "center",
                        margin: "auto",
                        border: "0.5px solid black",
                        fontFamily: "Scada"
                    }}>
                        {foldNumb}
                    </h1>
                </div>
            </div>
        </div>
    );
}

//ÜSTTEN 2.BLOK KOMPONENT İZİN TÜRÜ ALINIR, BİR KOMPONENTİ DE İÇERDE YARATIR.
function displayPermissionTypeAndRest(perTypeID) {
    return (
        <div style={{
            display: "flex",
            flexDirection: "row",
            flex: 6
        }}>
            <div style={{
                display: "flex",
                flex: 1,
                justifyContent: "center",
                border: "0.5px solid black",
                borderRadius: "2%",
