function dateConverter(givenDate1) {
    let givenDate= new Date(givenDate1);
    return givenDate.getDate() + "/" +
        (givenDate.getMonth() + 1) + "/" +
        givenDate.getFullYear()
        + "-" +
        givenDate.getHours() +
        ":" +
        givenDate.getMinutes();
}

