import React from "react";
import {Link} from "react-router-dom";
import {Row} from "react-bootstrap";
import Col from "react-bootstrap/Col";

{/*
    This class allows to chiefs to navigate through pages :
    1) "Create Permission Demand" (Because of that a manager is also a employee and needs create permission demands.)
    2) "Display Permission Demands" (Chief should be able to display demands of employee and himself/herself.   )
*/}

{/*

    This function includes two buttons.This buttons navigates the user as following:
    1) Create Permission Demands ( Navigates : "PermissionFilling")
    2) Display Permission Demands (Navigates : "DisplayPermissionsForChief" )

*/}

function NavigateTheChief() {
    return (
        /*
        * Main <div> defined here.
        */
        <div>
            <Col>
                <Row style={{
                    display: "flex",
                    flex: 1,
                    justifyContent: "center",
                    alignItems: "center",
                    marginTop: "10vw",
                    marginBottom: "20vw"
                }}>
                    {/*

                       Here the link that navigates the user to "Create Permission Demand".

                    */}
                    <Link to="PermissionFilling" style={{
                        textDecoration: "none", display: "flex", flex: 1,
                        height: "20vw"
                    }}>
                        <button type="button" className="btn btn-success" style={{
                            display: "flex",
                            marginRight: "5px",
                            flex: 1,
                            height: "20vw",
                            justifyContent: "center",
                            borderRadius: "5%",
                            textAlign: "center",
                            fontWeight: "normal"
                        }}>
                            <h1 style={{
                                display: "flex",
                                flex: 1,
                                justifyContent: "center",
                                fontSize: "3vw",
                                margin: "auto"
                            }}>
                                İZİN TALEBİ YARAT
                            </h1>
                        </button>
                    </Link>

                    {/*

                       Here the link that navigates the user to "Display Previous Permissions".


                    */}

                    <Link to="PreviousPermissionsChief" style={{
                        textDecoration: "none", display: "flex", flex: 1,
                        height: "20vw"
                    }}>
                        <button type="button" className="btn btn-success" style={{
                            display: "flex",
                            flex: 1,
                            height: "20vw",
                            borderRadius: "5%",
                            textAlign: "center",
                            justifyContent: "center"
                        }}>
                            <h1 style={{
                                display: "flex",
                                flex: 1,
                                justifyContent: "center",
                                fontSize: "3vw",
                                margin: "auto"
                            }}>
                              İZİN TALEPLERİNİ GÖRÜNTÜLE
                            </h1>
                        </button>
                    </Link>

                </Row>
            </Col>
        </div>
    );
}
export default NavigateTheChief;
