import React from "react";
import Logo from "./logok.png";


function PerPerDemScreen() {
    return (
        <div style={{
            display: "flex",
            flexDirection: "column",
            flex: 9,
            border:"0.2px solid black",
            borderRadius:"2%"
        }}>
            <div
                style={{
                    display: "flex",
                    flex: 1,
                    marginTop:"4px",
                    marginBottom:"4px",
                    border:"0.6px solid black"
                }}>
                {headPart()}
            </div>

            <div style={{
                display: "flex",
                flex: 1,
                marginTop:"4px",
                marginBottom:"4px",
                border:"0.6px solid black"
            }}>
                {underHeadPart()}
            </div>
            <div style={{
                display: "flex",
                flex: 1,
                marginTop:"4px",
                marginBottom:"4px",
                border:"0.6px solid black"
            }}>
                {personelInfoPart()}
            </div>
            <div style={{
                display: "flex",
                flex: 1,
                marginTop:"4px",
                marginBottom:"4px",
                border:"0.6px solid black"
            }}>
                {personelDatesAndExp()}
            </div>
            <div style={{
                display: "flex",
                flex: 1,
                marginTop:"4px",
                marginBottom:"4px",
                border:"0.6px solid black"
            }}>
                {vhcUsageOfPers()}
            </div>
            <div style={{
                display: "flex",
                flex: 1,
                marginTop:"4px",
                marginBottom:"4px",
                border:"0.6px solid black"
            }}>
                {perExplPart()}
            </div>
            <div style={{
                display: "flex",
                flex: 1,
                marginTop:"4px",
                marginBottom:"4px",
                border:"0.6px solid black"
            }}>
                {vhclUsgInfo()}
            </div>
            <div style={{
                display: "flex",
                flex: 1,
                marginTop:"4px",
                marginBottom:"4px",
                border:"0.6px solid black"
            }}>
                {perAccpPart()}
            </div>

            <div style={{
                display: "flex",
                flex: 1,
                marginTop:"4px",
                marginBottom:"4px",
                border:"0.6px solid black"
            }}>
                {formDetailsPart()}
            </div>
        </div>
    )
}

//ÜSTTEN 2.BLOK KOMPONENT İZİN TÜRÜ ALINIR, BİR KOMPONENTİ DE İÇERDE YARATIR.
function underHeadPart() {
    return (
