import React from "react";
import BootstrapSwitchButton from 'bootstrap-switch-button-react'

export function acceptionSymb() {
    return(
        <div style={{
            display:"flex",
            margin:"auto",
            fontSize:"20px",
            background:"rgb(65,222,36)"

        }}>
            ✔
        </div>
    )
}
export function deniedSymb() {
    return(
        <div style={{
            display:"flex",
            margin:"auto",
            fontSize:"20px",
            background:"rgb(203,27,27)"

        }}>
            ✖
        </div>
    )
}



export function prevPermission(props) {
    let izinTalepID=22;
    let izinTalepTarihi="22/22/2222";
    let izinBaşlangıcTarihi = "22/22/2222";
    let izinBitisTarihi = "22/22/2222";
    let amirOnayDurum = "✖";
    let genelOnayDurum ="✔";


    if (props) {
        return (
            <div style={{
                display:"flex",
                flexDirection:"column",
                justifyContent:"row",
                marginTop:"8px",
                border:"0.2px solid black",
                padding:"5px"
            }}>
                <label style={{
                    justifyContent:"left",
                    border:"0.5px solid black",
                    flex:6,
                    borderRadius:"2%",
                    display:"flex",
                    background:"rgba(174,215,184,0.4)"}}
                >
                    <h1 style={{
                        display:"flex",
                        flexDirection:"row",
                        flex:1,
                        fontSize:"14px",
                        padding:"3px",

                    }}>
                        {izinTalepID}
                    </h1>

                    <h1 style={{
                        display:"flex",
                        flexDirection:"row",
                        flex:1,
                        fontSize:"14px",
                        padding:"3px",

                    }}>
                        {izinTalepTarihi}
                    </h1>


                    <h1 style={{
                        display:"flex",
                        flexDirection:"row",
                        flex:1,
                        fontSize:"14px",
                        padding:"3px",

                    }}>
                        {izinBaşlangıcTarihi}
                    </h1>

                    <h1 style={{
                        display:"flex",
                        flexDirection:"row",
                        flex:1,
                        fontSize:"14px",
                        padding:"3px",

                    }}>
                        {izinBitisTarihi}
                    </h1>

                    <div style={{
                        display:"flex",
                        flexDirection:"row",
                        flex:1,
                        fontSize:"14px",
                        padding:"3px"

                    }}>
                            {acceptionSymb()}
                    </div>

                    <div style={{
                        display:"flex",
                        flexDirection:"row",
                        flex:1,
                        fontSize:"14px",
                        padding:"3px"

                    }}>
                            {deniedSymb()}
                    </div>



                </label>
            </div>
        )
    }
    return null;
}

class PreviousPermissons extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            displayActives: true,
        }
        this.ToggleButton = this.ToggleButton.bind(this);
    }

    ToggleButton() {
        this.setState((currentState) => ({
            displayActives: !this.state.displayActives
        }));
    }

    render() {
        return (
            <div style={{
                display: "flex",
                flexDirection: "column",
                flex: 6
            }}>
                <div style={{
                    display: "flex",
                    flexDirection: "row",
                    flex: 1,
                    justifyContent: "center",
                    margin: "25px"
                }}>
                    <BootstrapSwitchButton
                        style={{
                            display: "flex",
                            flex: 1
                        }}
                        checked={true}
                        onlabel='AKTİF TALEPLER'
                        offlabel='GEÇMİŞ TALEPLER'
                        width={350}
                        onChange={() => this.ToggleButton()}
                    />
                </div>
                <div style={{
                    display: "flex",
                    flexDirection: "row",
                    flex: 1,
                    justifyContent: "flex-end",
                    textAlign: "center"
                }}>
                    <h1
                        style={{
                            display: "flex",
                            flexDirection: "row",
                            justifyContent: "center",
                            flex: 0.6,
                            fontFamily: "Roboto",
                            fontSize: "15px",
                            textShadow: "0px 4px 4px rgba(0, 0, 0, 0.25)",
                            borderRight: "1px solid black",
                            textAlign: "center",
                            border: "1px solid black",
                            borderRadius: "3%",
                            margin: "2px",
                            paddingTop: "10px",
                            paddingBottom: "10px"
                        }}>
                        İZİN BİLGİLERİ
                    </h1>
                    <h1
                        style={{
                            display: "flex",
                            flexDirection: "row",
                            justifyContent: "center",
                            flex: 0.2,
                            fontFamily: "Roboto",
                            fontSize: "15px",
                            textShadow: "0px 4px 4px rgba(0, 0, 0, 0.25)",
                            textAlign: "center",
                            border: "1px solid black",
                            borderRadius: "3%",
                            margin: "2px",
                            paddingTop: "10px",
                            paddingBottom: "10px"

                        }}>
                        AMİR ONAYI
                    </h1>
                    <h1
                        style={{
                            display: "flex",
                            flexDirection: "row",
                            justifyContent: "center",
                            flex: 0.2,
                            fontFamily: "Scada",
                            fontSize: "15px",
                            textShadow: "0px 4px 4px rgba(0, 0, 0, 0.25)",
                            textAlign: "center",
                            border: "1px solid black",
                            borderRadius: "3%",
                            margin: "2px",
                            paddingTop: "10px",
                            paddingBottom: "10px"
                        }}>
                        GENEL YÖNETİCİ ONAYI
                    </h1>
                </div>
                {prevPermission(this.state.displayActives)}
            </div>
        )
    }
}

export default PreviousPermissons;
