import ReactDOM from "react-dom";
import App from "./App";
import {createStore} from "redux";

// Importing the Bootstrap CSS
import 'bootstrap/dist/css/bootstrap.min.css';
//ACTION INCREMENT
const increment =() => {
    return {
        type:"INCREMENT"
    }
}


//ACTION DECREMENT
const decrement =() => {
    return {
        type:"DECREMENT"
    }
}

//REDUCER
const counter =(state=0,action)=>{
    switch (action.type) {
        case "INCREMENT":
            return  state+1;
        case "DECREMENT":
            return state-1;
    }
}



