import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TablePagination from '@material-ui/core/TablePagination';
import TableRow from '@material-ui/core/TableRow';
import {Button} from "react-bootstrap";

const columns = [
    { id: 'name', label: 'İzin ID', display: "flex" ,justifyContent:"flex-start"
    },
    { id: 'code', label: 'İzin Talep Tarihi',  display: "flex" ,justifyContent: "flex-start"},
    {
        id: 'population',
        label: 'İzin Başlangıç',

        display:"flex",
        justifyContent:"flex-start",
    },
    {
        id: 'size',
        label: 'İzin Bitiş',
        display: "flex",
        justifyContent: "flex-start"
    },
    {
        id: 'chiefAcception',
        label: 'Amir Onayı',
        display: "flex",
        justifyContent: "flex-start"
    },
    {
        id: 'chiefAcception',
        label: 'Genel Müdür Onayı',
        display: "flex",
        justifyContent: "flex-start"
    },
    {
        id: 'chiefAcception',
        label: 'Önizleme',
        display: "flex",
        justifyContent: "flex-start"
    },
];
function btnReturner() {
    return(
        <div>
            <Button>
                MRB
            </Button>
        </div>
    )

}


function createData(name, code, population, size,chiefAccp,bossAcpt) {
    return { name, code, population, size , chiefAccp,bossAcpt};
}

const rows = [
    createData('India', 'IN', "22/22/2222", 3287263,1,1),
    createData('China', 'CN', "22/22/2222", 9596961,1,2),
    createData('India', 'IN', "22/22/2222", 3287263,1,1),
    createData('China', 'CN', "22/22/2222", 9596961,1,2),
    createData('China', 'CN', "22/22/2222", 9596961,1,2),
    createData('China', 'CN', "22/22/2222", 9596961,1,2),
    createData('China', 'CN', "22/22/2222", 9596961,1,2),
    createData('China', 'CN', "22/22/2222", 9596961,1,2),
    createData('China', 'CN', "22/22/2222", 9596961,1,2),
    createData('China', 'CN', "22/22/2222", 9596961,1,2),
    createData('China', 'CN', "22/22/2222", 9596961,1,2),
    createData('China', 'CN', "22/22/2222", 9596961,1,2),
    createData('China', 'CN', "22/22/2222", 9596961,1,2),
];

const useStyles = makeStyles({
    root: {
        width: '100%',
    },
    container: {
        maxHeight: 440,
    },
});

export default function StickyHeadTable() {
    const classes = useStyles();
    const [page, setPage] = React.useState(0);
    const [rowsPerPage, setRowsPerPage] = React.useState(10);

    const handleChangePage = (event, newPage) => {
        setPage(newPage);
    };

    const handleChangeRowsPerPage = (event) => {
        setRowsPerPage(+event.target.value);
        setPage(0);
    };

    return (
        <Paper className={classes.root}>
            <TableContainer className={classes.container}>
                <Table stickyHeader aria-label="sticky table">
                    <TableHead>
                        <TableRow>
                            {columns.map((column) => (
                                <TableCell
                                    key={column.id}
                                    align={column.align}
                                    style={{ minWidth: column.minWidth }}
                                >
                                    {column.label}
                                </TableCell>
                            ))}
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {rows.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage).map((row) => {
                            return (
                                <TableRow hover role="checkbox" tabIndex={-1} key={row.code}>
                                    {columns.map((column) => {
                                        const value = row[column.id];
                                        return (
                                            <TableCell key={column.id}>
                                                {value}
                                            </TableCell>
                                        );
                                    })}
                                </TableRow>
                            );
                        })}
                    </TableBody>
                </Table>
            </TableContainer>
            <TablePagination
                rowsPerPageOptions={[10, 25, 100]}
                component="div"
                count={rows.length}
                rowsPerPage={rowsPerPage}
                page={page}
                onChangePage={handleChangePage}
                onChangeRowsPerPage={handleChangeRowsPerPage}
            />
        </Paper>
    );
}
