import React from 'react';
import {makeStyles} from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TablePagination from '@material-ui/core/TablePagination';
import TableRow from '@material-ui/core/TableRow';
import Button from "@material-ui/core/Button";
import {createMuiTheme, ThemeProvider} from '@material-ui/core/styles';
import {trTR} from '@material-ui/core/locale';
import BootstrapSwitchButton from "bootstrap-switch-button-react";
import {Bounce} from "react-reveal";
import Grid from "@material-ui/core/Grid";
import {Link} from "react-router-dom";


const theme = createMuiTheme({
    palette: {
        primary: {main: '#1976d2'},
    },
}, trTR);

const columns = [
    {id: 'demandID', label: 'İzin ID'},
    {id: 'demandDate', label: 'TALEP TARİHİ'},
    {
        id: 'demandBegin',
        label: 'İZİN BAŞLANGIÇ',
    },
    {
        id: 'demandEnd',
        label: 'İZİN BİTİŞ',
    },
    {
        id: 'chiefAcceptance',
        label: 'AMİR ONAYI',
    },
    {
        id: 'generalManAcceptance',
        label: 'GENEL MÜDÜR ONAYI',
    },
    {
        id: 'displayScreen',
        label: ' ÖNİZLEME',
    },
];


function createData(demandID, demandDate, demandBegin, demandEnd, chiefStatus, bossStatus) {
    return {demandID, demandDate, demandBegin, demandEnd, chiefStatus, bossStatus};
}

const rows = [
    createData('123', "22/22/2222", "22/22/2222", "22/22/2222", 1, 0),
    createData('124', "22/22/2221", "22/22/2222", "22/22/2222", 1, 0),
    createData('125', "22/22/22212", "22/22/2222", "22/22/2222", 1, 0),
    createData('126', "22/22/22122", "22/22/2222", "22/22/2222", 1, 0),
    createData('127', "22/22/2112", "22/22/22", "22/22/22", 1, 0),
    createData('128', "22/22/22322", "22/22/2222", "22/22/2222", 1, 0),
    createData('129', "22/22/22522", "22/22/2222", "22/22/2222", 1, 0),
    createData('130', "22/22/22822", "22/22/2222", "22/22/2222", 1, 0),
    createData('131', "22/22/22*22", "22/22/2222", "22/22/2222", 1, 0),
    createData('132', "22/22/22722", "22/22/2222", "22/22/2222", 1, 0),
    createData('133', "22/22/22222", "22/22/2222", "22/22/2222", 1, 0),
    createData('134', "22/22/27222", "22/22/2222", "22/22/2222", 1, 0),
    createData('135', "22/22/22s22", "22/22/2222", "22/22/2222", 1, 0),
    createData('136', "22/22/22g22", "22/22/2222", "22/22/2222", 1, 0),
    createData('137', "22/22/222a2", "22/22/2222", "22/22/2222", 1, 0)
];

function chiefBossAccaptance(chiefStatus) {
    if (chiefStatus === 1) {
        return (
            <div style={{
                display: "flex", width: "80px", justifyContent: "center",
                textAlign: "center"

            }}>
                <h1 style={{
                    backgroundColor: "rgba(49,236,9,0.95)",
                    fontSize: "20px",
                    borderRadius: "50%",
                    margin: "auto",
                    textAlign: "center",
                    width: "40px",
                }}
                >
                    ✔
                </h1>
            </div>
        )
    } else if (chiefStatus === 2) {
        return (
            <div style={{
                display: "flex", width: "80px", justifyContent: "center"
            }}>
                <h1 style={{
                    backgroundColor: "rgba(220,16,16,0.95)",
                    fontSize: "20px",
                    borderRadius: "50%",
                    margin: "auto",
                    textAlign: "center",
                    display: 'flex',
                    width: "40px"
                }}
                >
                    ✖
                </h1>
            </div>
        )
    } else {
        return (
            <div style={{
                display: "flex", width: "80px",
                justifyContent: "center"
            }}>
                <h1 style={{
                    backgroundColor: "rgba(255,220,0,0.95)",
                    fontSize: "20px",
                    borderRadius: "50%",
                    margin: "auto",
                    textAlign: "center",
                    width: "40px"
                }}
                >
                    ⊖
                </h1>
            </div>
        )
    }
}

function displayButton() {
    return (
        <div style={{
            display: "flex",
            justifyContent: "flex-start"
        }}>
            <Link to={"/PersonelScreens/PermissionDisplay"}>
                <Button variant="outlined" color="primary" style={{
                    display: "flex",
                    justifyContent: "flex-start",
                    padding: "1px",
                    background: "rgba(180,133,205,0.64)",
                    borderRadius: "50%"
                }}>
                    <img style={{width: "30px", height: "20px", margin: "auto"}} src={require('./eye2.svg')}
                         alt="my image"/>
                </Button>
            </Link>
        </div>
    )
}


const useStyles = makeStyles({
    root: {
        width: '100%',
    },
    container: {
        display: "flex",
    },
});

const stylesForBounce = makeStyles((theme) => ({
    root: {
        flexGrow: 1,
    },
    paper: {
        textAlign: 'center',
        padding: "8px",
        background: "rgba(139,133,133,0.21)"
    },
}));

const [page, setPage] = React.useState(0);
const [rowsPerPage, setRowsPerPage] = React.useState(10);
const styke = stylesForBounce();

const handleChangePage = (event, newPage) => {
    setPage(newPage);
};

const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(+event.target.value);
    setPage(0);
};

export default class StickyHeadTable {
    constructor(props) {
        const classes = useStyles();
        this.FormRow = this.FormRow.bind(this);
    }

    FormRow() {
        return (
            <React.Fragment>
                <Grid item xs={3}>
                    <Paper className={styke.paper}>
                        <h1 style={{
                            fontSize: "20px",
                            borderRadius: "50%",
                            backgroundColor: "rgba(49,236,9,0.95)",
                            textAlign: "center"
                        }}>
                            ✔
                        </h1>
                        <h1 style={{
                            fontSize: "12px",
                            fontWeight: "bold",
                            textAlign: "center",
                            paddingRight: "10px"
                        }}
                        >
                            ONAY
                        </h1>

                    </Paper>
                </Grid>
                <Grid item xs={6}>
                    <Paper className={styke.paper}>

                        <h1 style={{
                            marginRight: "20px",
                            marginLeft: "20px",
                            fontSize: "20px",
                            borderRadius: "50%",
                            backgroundColor: "rgba(255,220,0,0.95)",
                            textAlign: "center"
                        }}>
                            ⊖
                        </h1>
                        <h1 style={{fontSize: "12px", textAlign: "center", fontWeight: "bold",}}
                        >
                            BEKLEMEDE
                        </h1>

                    </Paper>
                </Grid>
                <Grid item xs={3}>
                    <Paper className={styke.paper}>
                        <h1 style={{
                            fontSize: "20px",
                            borderRadius: "50%",
                            backgroundColor: "rgba(220,16,16,0.95)",
                            textAlign: "center"
                        }}>
                            ✖
                        </h1>
                        <h1 style={{
                            fontSize: "12px",
                            fontWeight: "bold",
                            textAlign: "center"
                        }}
                        >
                            RED
                        </h1>

                    </Paper>
                </Grid>


            </React.Fragment>
        );
    }
    render() {
        return (
            <div>
                <div style={{display: "flex", justifyContent: "flex-end", margin: "5px"}}>
                    <Bounce>
                        <Grid container spacing={1}>
                            <Grid container item xs={12} spacing={1}>
                                <FormRow/>
                            </Grid>
                        </Grid>
                    </Bounce>
                </div>
                <ThemeProvider theme={theme}>
                    <Paper className={this.classes.root}>
                        <TableContainer className={this.classes.container}>
                            <Table stickyHeader aria-label="sticky table">
                                <TableHead>
                                    <TableRow>
                                        {columns.map((column) => (
                                            <TableCell
                                                key={column.id}
                                            >
                                                {column.label}
                                            </TableCell>
                                        ))}
                                    </TableRow>
                                </TableHead>
                                <TableBody>
                                    {rows.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage).map((row) => {
                                        return (
                                            <TableRow hover role="checkbox" tabIndex={-1} key={row.demandDate}>
                                                {columns.map((column) => {
                                                    const value = row[column.id];
                                                    return (
                                                        <TableCell key={column.id}>
                                                            {value}
                                                            {column.id === "chiefAcceptance" && chiefBossAccaptance(row.chiefStatus)}
                                                            {column.id === "generalManAcceptance" && chiefBossAccaptance(row.bossStatus)}
                                                            {column.id === "displayScreen" && displayButton()}
                                                        </TableCell>
                                                    );
                                                })}
                                            </TableRow>
                                        );
                                    })}
                                </TableBody>
                            </Table>
                        </TableContainer>
                        <TablePagination
                            rowsPerPageOptions={[10, 25, 100]}
                            component="div"
                            count={rows.length}
                            rowsPerPage={rowsPerPage}
                            page={page}
                            onChangePage={handleChangePage}
                            onChangeRowsPerPage={handleChangeRowsPerPage}

                        />
                    </Paper>
                </ThemeProvider>
            </div>
        )
    };
}
