                <div style={{
                    display: "flex",
                    flexDirection: "row",
                    flex: 1,
                    borderBottom: "0.1px solid black"
                }}>
                    <h1 style={{
                        display: "flex",
                        flex: 0.8,
                        justifyContent: "center",
                        fontFamily:"Scada",
                        fontSize: "18px",
                        margin:"auto"
                    }}>
                        Diğer
                    </h1>
                    <input type="checkbox" style={{
                        display: "flex",
                        flex: 0.2,
                        height:"80%",
                        margin:"auto"
                    }}/>
                </div>
            </div>
            <div style= {{
                display: "flex",
                flexDirection: "row",
                flex: 3
            }}>
                <div style={{
                    display: "flex",
                    flexDirection: "row",
                    flex: 1,
                    borderRight:"0.1px solid black"
                }}>
                    <h1 style={{
                        display: "flex",
