                display: "flex",
                flexDirection: "row",
                flex: 4,
                flexWrap: "nowrap",
                justifyContent: "flex-start",
                alignItems: "center",
                border: "1px solid black",
                borderTop: "1px solid black",
                borderRight: "0.2px solid black"
