import React from "react";
import {Link} from "react-router-dom";
import {Row} from "react-bootstrap";
import Col from "react-bootstrap/Col";
import ThemeProvider from "react-bootstrap/ThemeProvider";
import Button from "@material-ui/core/Button";
import createMuiTheme from "@material-ui/core/styles/createMuiTheme";
import makeStyles from "@material-ui/core/styles/makeStyles";
import green from "@material-ui/core/colors/green";
import withStyles from "@material-ui/core/styles/withStyles";

function PersonelNavigation() {
    const iconFirst = require('./fromSVG.svg');
    const iconSecond = require('./displayIcon.svg');
    return (
        <div>
            <Col style={{
                display: "flex",
                flexDirection: "row",
                justifyContent: "column",
                flex: 2
            }}>
                <Row style={{
                    display: "flex",
                    flexDirection: "row",
                    flex: 2,
                    justifyContent: "center",
                    alignItems: "center",
                    margin: "25px"
                }}>
                    <Link to="PermissionFilling" style={{
                        alignItems: "center",
                        justifyContent: "center",
                        margin: "15px",
                        flex:1
                    }} xs={12} md={12}>
                        <button type="button" className="btn btn-success" style={{display:"flex",flexWrap:"wrap",flex:1}} >
                            <img  style={{display:"flex",height:"12vw",width:"12vw",margin:"10px",flex:0.5}} src={iconFirst}/>
                            <h3 style={{display:"flex",justifyContent:"center",margin:"auto",fontSize:"2.5vw",flex:0.5}}>
                                İZİN TALEBİ YARAT
                            </h3>
                        </button>
                    </Link>
                    <Link to="PreviousPermissons"  xs={12} md={12} style={{
                        alignItems: "center",
                        justifyContent: "center",
                        flex:1
                    }}>
                        <button type="button" className="btn btn-success" style={{display:"flex",flexWrap:"wrap",flex:1}}>
                            <img  style={{display:"flex",height:"12vw",width:"12vw",margin:"10px",flex:0.5}} src={iconSecond}/>
                            <h3  style={{display:"flex",justifyContent:"center",margin:"auto",fontSize:"2.5vw",flex:0.5}}>
                                GEÇMİŞ İZİNLERİ GÖRÜNTÜLE
                            </h3>
                        </button>

                    </Link>
                </Row>
            </Col>
        </div>
    );
}



export default PersonelNavigation;
