import React from "react";
import {Link} from "react-router-dom";
import {Row} from "react-bootstrap";
import Col from "react-bootstrap/Col";
import ThemeProvider from "react-bootstrap/ThemeProvider";
import Button from "@material-ui/core/Button";
import createMuiTheme from "@material-ui/core/styles/createMuiTheme";
import makeStyles from "@material-ui/core/styles/makeStyles";
import green from "@material-ui/core/colors/green";

const theme = createMuiTheme({
    palette: {
        primary: green,
    },
});

const useStyles = makeStyles((theme) => ({
    margin: {
        margin: theme.spacing(1),
    },
}));

function PersonelNavigation() {
    const classes = useStyles();
    const iconFirst = require('./fromSVG.svg');
    const iconSecond = require('./displayIcon.svg');
    return (
        <div>
            <Col style={{
                display: "flex",
                flexDirection: "row",
                justifyContent: "column",
                flex: 2
            }}>
                <Row style={{
                    display: "flex",
                    flexDirection: "row",
                    flex: 2,
                    justifyContent: "center",
                    alignItems: "center",
                    margin: "25px"
                }}>
                    <Link to="PermissionFilling" style={{
                        display: "block",
                        alignItems: "center",
                        justifyContent: "center",
                        margin: "15px"
                    }}>
                        <Button variant="outlined" color="primary">
                                <img  style={{ height:"5vw",width:"5vw",margin:"10px"}} src={iconFirst}/>
                            İZİN TALEBİ YARAT
                        </Button>
                    </Link>
                    <Link to="PreviousPermissons" style={{
                        display: "block",
                        alignItems: "center",
                        justifyContent: "center",
                    }}>
                        <ThemeProvider theme={theme}>
                            <Button variant="outlined" color="primary">
                                <img  style={{height:"5vw",width:"5vw",margin:"10px"}} src={iconSecond}/>
                                GEÇMİŞ İZİNLERİ GÖRÜNTÜLE
                            </Button>
                        </ThemeProvider>
                    </Link>
                </Row>
            </Col>
        </div>
    );
}



export default PersonelNavigation;
