import React from "react";
import Logo from "./SystemImages/CompanyLogo.png";
import {Link} from "react-router-dom";
import {Row} from "react-bootstrap";
import Col from "react-bootstrap/Col";
import {connect} from "react-redux";
import moment from "moment";




const mapStateToProps = (state) => {
    return {
        userID:state.permissionReducer.userID,

        userType:state.permissionReducer.userType,
        displayStatus:state.permissionReducer.displayStatus,

        personalName:state.permissionReducer.personalName,

        demandDate:moment().format("DD-MM-YYYY HH:mm:ss"),

        beginDateOfPermission:state.permissionReducer.beginDateOfPermission,
        endDateOfPermission:state.permissionReducer.endDateOfPermission,

        foldCode:state.permissionReducer.foldCode,
        areaCode:state.permissionReducer.areaCode,

        selectVehicleUsageType:state.permissionReducer.selectVehicleUsageType,
        permissionDescription: state.permissionReducer.permissionDescription,

        personalCarUsage:state.permissionReducer.personalCarUsage,
        totalDistanceOfIndividualCar:state.permissionReducer.totalDistanceOfIndividualCar,
        priceOfTrainOrBus:state.permissionReducer.priceOfTrainOrBus,

        displayThePermissionName: state.permissionReducer.displayThePermissionName,
        setPermissionType:state.permissionReducer.setPermissionType

    }
};

class DisplayPermissionForm extends React.Component{
    constructor() {
        super();

    // Mock data = CRUD = (Araştır)

    }
    render() {

        let permissionLength = "22 saat";

        return (
            <div style={{
                display: "flex",
                flexDirection: "column",
                flex: 10
            }}>
                <div style={{
                    display: "flex",
                    flexDirection: "column",
                    flex: 9,
                    border: "0.2px solid black"
                }}>
                    <div
                        style={{
                            display: "flex",
                            flex: 1,
                            marginTop: "4px",
                            marginBottom: "4px",
                            border: "0.6px solid black"
                        }}>
                        {displayLogoAndMainDescription(this.props.areaCode, this.props.foldCode)}
                    </div>

                    <div style={{
                        display: "flex",
                        flex: 1,
                        marginTop: "4px",
                        marginBottom: "4px",
                        border: "0.6px solid black"
                    }}>
                        {displayPermissionTypeAndRest(this.props.setPermissionType)}
                    </div>
                    <div style={{
                        display: "flex",
                        flex: 1,
                        marginTop: "4px",
                        marginBottom: "4px",
                        border: "0.6px solid black"
                    }}>
                        {displayPersonalInformationPart(this.props.personalName, this.props.demandDate)}
                    </div>
                    <div style={{
                        display: "flex",
                        flex: 1,
                        marginTop: "4px",
                        marginBottom: "4px",
                        border: "0.6px solid black"
                    }}>
                        {

                            displayEmployeesPermissionDates(
                            this.props.beginDateOfPermission.getUTCDate() +"-"+
                                (this.props.beginDateOfPermission.getUTCMonth() + 1)  +"-"+
                                this.props.beginDateOfPermission.getUTCFullYear()
                            ,
                                this.props.beginDateOfPermission.getUTCHours()+
                                ":"+
                                this.props.beginDateOfPermission.getUTCMinutes(),

                            this.props.endDateOfPermission.getDay() +"-"+
                            (this.props.endDateOfPermission.getMonth() + 1) +"-"+
                            this.props.endDateOfPermission.getFullYear(),

                                this.props.endDateOfPermission.getUTCHours()+
                                ":"+
                                this.props.endDateOfPermission.getUTCMinutes(),
                            permissionLength)}
                    </div>
                    <div style={{
                        display: "flex",
                        flex: 1,
                        marginTop: "4px",
                        marginBottom: "4px",
                        border: "0.6px solid black"
                    }}>
                        {displayVehicleUsagesOfPersonel(this.props.selectVehicleUsageType)}
                    </div>
                    <div style={{
                        display: "flex",
                        flex: 1,
                        marginTop: "4px",
                        marginBottom: "4px",
                        border: "0.6px solid black"
                    }}>
                        {displayTheExplanationOfPermission(this.props.permissionDescription)}
                    </div>
                    <div style={{
                        display: "flex",
                        flex: 1,
                        marginTop: "4px",
                        marginBottom: "4px",
                        border: "0.6px solid black"
                    }}>
                        {displayDetailsOfVehicleUsage(this.props.totalDistanceOfIndividualCar, this.props.priceOfTrainOrBus, this.props.personalCarUsage)}
                    </div>
                    <div style={{
                        display: "flex",
                        flex: 1,
                        marginTop: "4px",
                        marginBottom: "4px",
                        border: "0.6px solid black"
                    }}>
                        {displayTheAcceptionPart()}
                    </div>

                    <div style={{
                        display: "flex",
                        flex: 1,
                        marginTop: "4px",
                        marginBottom: "4px",
                        border: "0.6px solid black"
                    }}>
                        {displayFormHRrelatedInfo()}
                    </div>
                </div>
                <div style={{
                    display: "flex",
                    flex: 1,
                    marginTop: "4px",
                    marginBottom: "4px"
                }}>

                    {this.props.displayStatus === 1 && (this.props.userType === 1 ? displayManagersButtonsForForm() : displayPersonelsButtonsForForm())}
                </div>

            </div>
        )
    }
}

//ÜST BAŞLIĞIN OLDUĞU KOMPONENT ( __1__ )
function displayLogoAndMainDescription(areaCode, foldNumb) {
    return (
        <div style={{
            display: "flex",
            flexDirection: "row",
            flex: 5,
            border: "0.1px solid black"
        }}>
            <img src={Logo} alt="Logo" style={{
                display: "flex",
                flex: 1,
                width: "200px",
                height: "100px",
                margin: "0.1vw",
                border: "0.1px solid black",
                borderRadius: "2%"
            }}/>
            <div style={{
                display: "flex",
                flexDirection: "column",
                flex: 4,

            }}>
                <div style={{
                    display: "flex",
                    flex: 2,
                    justifyContent: "flex-start",
                    border: "0.1px solid black",
                    margin: "0.1vw"
                }}>
                    <h1 style={{
                        display: "flex",
                        flex: 1.8,
                        justifyContent: "center",
                        fontSize: "3vw",
                        fontWeight: "normal",
                        margin: "auto",
                        fontFamily: "Scada"
                    }}>
                        DESIRD TASARIM
                    </h1>

                    <h1 style={{
                        display: "flex",
                        flex: 0.2,
                        fontSize: "2.5vw",
                        justifyContent: "center",
                        margin: "auto",
                        fontFamily: "Scada"
                    }}>
                        {areaCode}
                    </h1>
                </div>

                <div style={{
                    display: "flex",
                    flex: 2,
                    justifyContent: "flex-start",
                    border: "0.1px solid black",
                    margin: "0.1vw"
                }}>
                    <h1 style={{
                        display: "flex",
                        flex: 1.9,
                        textAlign: "center",
                        justifyContent: "center",
                        fontSize: "2.5vw",
                        fontWeight: "normal",
                        margin: "auto",
                        fontFamily: "Scada"
                    }}>
                        PERSONEL İZİN BELGESİ FORMU
                    </h1>

                    <h1 style={{
                        display: "flex",
                        flex: 0.1,
                        fontSize: "2.5vw",
                        justifyContent: "center",
                        margin: "auto",
                        border: "0.5px solid black",
                        fontFamily: "Scada"
                    }}>
                        {foldNumb}
                    </h1>
                </div>
            </div>
        </div>
    );
}

//ÜSTTEN 2.BLOK KOMPONENT İZİN TÜRÜ ALINIR, BİR KOMPONENTİ DE İÇERDE YARATIR.
function displayPermissionTypeAndRest(perTypeID) {
    return (
        <div style={{
            display: "flex",
            flexDirection: "row",
            flex: 6
        }}>
            <div style={{
                display: "flex",
                flex: 1,
                justifyContent: "center",
                border: "0.5px solid black",
                borderRadius: "2%",
                textAlign: "center"
            }}>
                <h1 style={{
                    display: "flex",
                    flex: 1,
                    fontFamily: "Scada",
                    fontSize: "22px",
                    margin: "auto",
                    justifyContent: "center"
                }}>
                    İZİN TÜRÜ
                </h1>

            </div>
            <div style={{
                display: "flex",
                flex: 5
            }}>
                {perAccCompt(perTypeID)}
            </div>
        </div>
    );
}

//İZİN TİPİ SEÇME COMPONENTİ
function perAccCompt(perTypeID) {


    return (
        <div style={{
            display: "flex",
            flexDirection: "column",
            flex: 6,
            border: "0.2px solid black"
        }}>
            <div style={{
                display: "flex",
                flexDirection: "row",
                flex: 3
            }}>
                <div style={{
                    display: "flex",
                    flexDirection: "row",
                    flex: 1,
                    borderRight: "0.1px solid black",
                    borderBottom: "0.1px solid black"
                }}>
                    <h1 style={{
                        display: "flex",
                        justifyContent: "center",
                        flex: 0.8,
                        fontFamily: "Scada",
                        fontSize: "18px",
                        margin: "auto"
                    }}>
                        Yıllık İzin
                    </h1>

                    <input disabled checked={perTypeID==="1"}
                        type="checkbox" style={{
                        margin: "auto",
                        display: "flex",
                        flex: 0.2,
                        height: "80%",
                    }}/>
                </div>

                <div style={{
                    display: "flex",
                    flexDirection: "row",
                    flex: 1,
                    borderRight: "0.1px solid black",
                    borderBottom: "0.1px solid black"
                }}>
                    <h1 style={{
                        display: "flex",
                        flex: 0.8,
                        justifyContent: "center",
                        fontFamily: "Scada",
                        fontSize: "18px",
                        margin: "auto"
                    }}>
                        Görevli
                    </h1>

                    <input type="checkbox" disabled checked={perTypeID==="2"}
                           style={{
                        display: "flex",
                        flex: 0.2,
                        height: "80%",
                        margin: "auto"
                    }}/>
                </div>
                <div style={{
                    display: "flex",
                    flexDirection: "row",
                    flex: 1,
                    borderBottom: "0.1px solid black"
                }}>
                    <h1 style={{
                        display: "flex",
                        flex: 0.8,
                        justifyContent: "center",
                        fontFamily: "Scada",
                        fontSize: "18px",
                        margin: "auto"
                    }}>
                        Diğer
                    </h1>
                    <input type="checkbox" disabled checked={perTypeID==="6"} style={{
                        display: "flex",
                        flex: 0.2,
                        height: "80%",
                        margin: "auto"
                    }}/>
                </div>
            </div>
            <div style={{
                display: "flex",
                flexDirection: "row",
                flex: 3
            }}>
                <div style={{
                    display: "flex",
                    flexDirection: "row",
                    flex: 1,
                    borderRight: "0.1px solid black"
                }}>
                    <h1 style={{
                        display: "flex",
                        justifyContent: "center",
                        flex: 0.8,
                        fontSize: "14px",
                        margin: "auto",
                        fontFamily: "Scada"
                    }}>
                        Ücretsiz İzin
                    </h1>

                    <input type="checkbox" disabled checked={perTypeID==="5"}
                           style={{
                        margin: "auto",
                        display: "flex",
                        flex: 0.2,
                        height: "80%",
                    }}/>
                </div>

                <div style={{
                    display: "flex",
                    flexDirection: "row",
                    flex: 1,
                    borderRight: "0.1px solid black"
                }}>
                    <h1 style={{
                        display: "flex",
                        flex: 0.8,
                        justifyContent: "center",
                        fontSize: "16px",
                        fontFamily: "Scada",
                        margin: "auto"
                    }}>
                        Ücretli İzin
                    </h1>

                    <input type="checkbox" disabled checked={perTypeID==="3"} style={{
                        display: "flex",
                        flex: 0.2,
                        height: "80%",
                        margin: "auto"
                    }}/>
                </div>
                <div style={{
                    display: "flex",
                    flexDirection: "row",
                    flex: 1
                }}>
                    <h1 style={{
                        display: "flex",
                        flex: 0.8,
                        justifyContent: "center",
                        fontSize: "12px",
                        fontFamily: "Scada",
                        margin: "auto"
                    }}>
                        2 Ay İçinde Telafi Yapılacak
                    </h1>
                    <input type="checkbox" disabled checked={perTypeID==="4"} style={{
                        display: "flex",
                        flex: 0.2,
                        height: "80%",
                        margin: "auto"
                    }}/>
                </div>
            </div>
        </div>
    )
}

//This functional component fills that name-surname of personel that demands permisson and date when demans created.
function displayPersonalInformationPart(personalNameSurname, demandDate) {

    return (
        <div style={{
            display: "flex",
            flexDirection: "row",
            flex: 6,
        }}>
            <div style={{
                display: "flex",
                flexDirection: "row",
                flex: 4.333,
                border: "0.5px solid black"

            }}>
                <h1 style={{
                    display: "flex",
                    justifyContent: "center",
                    flex: 1,
                    textAlign: "center",
                    fontFamily: "Scada",
                    margin: "auto",
                    fontSize: "18px",
                    borderTop: "0px",
                    fontWeight: "normal"
                }}
                >AD SOYAD</h1>
                <div style={{
                    display: "flex",
                    justifyContent: "center",
                    flex: 3.333,
                    border: "0.5px solid black",
                    borderLeft: "1px solid black",
                    borderTop: "0px"
                }}
                >
                    <h1 style={{
                        display: "flex",
                        flex: 3.333,
                        justifyContent: "center",
                        margin: "auto",
                        fontFamily: "Scada",
                        fontSize: "15px",
                        textAlign: "center"
                    }}
                    >
                        {personalNameSurname}
                    </h1>
                </div>
            </div>
            <div style={{
                display: "flex",
                flex: 1.666,
                justifyContent: "center"
            }}>
                <h1 style={{
                    display: "flex",
                    flex: 1.667,
                    justifyContent: "center",
                    textAlign: "center",
                    margin: "auto",
                    fontSize: "18px",
                    fontFamily: "Scada",
                    borderBottom: "0.5px solid black",
                    borderRight: "0.5px solid black"
                }}
                >Talep Tarih : {demandDate}</h1>
            </div>
        </div>
    );
}

//BLOCK FOUR
function displayEmployeesPermissionDates(permissionStartDate, permissionStartTime, permissionEndDate, permissionEndTime, permissionLength) {
    return (
        <div style={{
