import React from "react";
import {Link} from "react-router-dom";
import Grid from '@material-ui/core/Grid';
import makeStyles from "@material-ui/core/styles/makeStyles";


const useStyles = makeStyles((theme) => ({
    root: {
        flexGrow: 1,
    },
    paper: {
        padding: theme.spacing(2),
        textAlign: 'center',
        color: theme.palette.text.secondary,
    },
}));


function displayIndividualOperations(flag) {
    const crateDemandIcon = require('./SystemImages/FillingFormImage.svg');
    if (flag) {
        return (
            <Grid container spacing={2} style={{width: "100%"}}>
                <Grid item xs={12} sm={6}>
                    <button style={{display: "flex", flex: 0.5}} type="button" className="btn btn-success">
                        <img alt="IconEye" style={{width: "50px", height: "50px"}}
                             src={crateDemandIcon}/>
                        <h1 style={{display:"flex",fontSize:"5vw"}}>
                            İZİN TALEBİ YARAT
                        </h1>
                    </button>
                </Grid>
                <Grid item xs={12} sm={6}>
                    <button style={{display: "flex", flex: 0.5}} type="button" className="btn btn-success">
                        <h1 style={{display:"flex",fontSize:"5vw"}} >
                            İZİNLERİMİ GÖRÜNTÜLE
                        </h1>
                    </button>
                </Grid>
            </Grid>
        )
    } else {
        return null;
    }
}


class NavigateTheChief extends React.Component {

    constructor() {
        super();
        this.state = {
            selectedIndividualOperations: false
        }
        this.handleOperationTypeSelection = this.handleOperationTypeSelection.bind(this);
    }

    handleOperationTypeSelection(event) {
        this.setState({
            selectedIndividualOperations: !this.state.selectedIndividualOperations,
        })

    }

    render() {
        const managementIcon = require('./SystemImages/ManagerIcon.svg');
        const individualIcon = require('./SystemImages/IndividualIcon.svg');


        return (
            <Grid container spacing={2} style={{width: "100%"}}>

                <Grid item xs={12} sm={6}>
                    <Link to="FillingThePermissionForm" style={{display: "flex", flex: 1, textDecoration: "none"}}>
                        <button type="button" className="btn btn-success"
                                style={{display: "flex", flex: 1, margin: "20px"}}>
                            <img alt="IconEye" style={{width: "150px", height: "150px", fill: "black"}}
                                 src={managementIcon}/>
                            <h3 style={{margin: "auto"}}>
                                İDARİ İŞLEMLERİM
                            </h3>
                        </button>
                    </Link>
                </Grid>

                <Grid item xs={12} sm={6} style={{display: "flex", flexDirection: "column", flex: 1}}>

                    <button type="button" className="btn btn-success" onClick={this.handleOperationTypeSelection}
                            style={{display: "flex", flex: 0.5, margin: "20px"}}>

                        <img style={{width: "130px", height: "130px"}} src={individualIcon} alt="IconEye"/>
                        <h3 style={{margin: "auto"}}>
                            KİŞİSEL İŞLEMLERİM
                        </h3>
                    </button>
                    <Link to="PreviousPermissons" style={{display: "flex", flex: 0.50, textDecoration: "none"}}>
                        {displayIndividualOperations(this.state.selectedIndividualOperations)}
                    </Link>
                </Grid>

            </Grid>

        );
    }
}

export default NavigateTheChief;
